xception Locked=\"false\" Priority=\"71\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Shading Accent 2\"/> <w:LsdException Locked=\"false\" Priority=\"72\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful List Accent 2\"/> <w:LsdException Locked=\"false\" Priority=\"73\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Grid Accent 2\"/> <w:LsdException Locked=\"false\" Priority=\"60\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Shading Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"61\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light List Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"62\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Grid Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"63\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 1 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"64\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 2 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"65\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 1 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"66\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 2 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"67\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 1 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"68\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 2 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"69\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 3 Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"70\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Dark List Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"71\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Shading Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"72\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful List Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"73\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Grid Accent 3\"/> <w:LsdException Locked=\"false\" Priority=\"60\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Shading Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"61\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light List Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"62\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Grid Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"63\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 1 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"64\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 2 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"65\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 1 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"66\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 2 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"67\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 1 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"68\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 2 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"69\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 3 Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"70\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Dark List Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"71\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Shading Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"72\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful List Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"73\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Grid Accent 4\"/> <w:LsdException Locked=\"false\" Priority=\"60\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Shading Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"61\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light List Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"62\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Grid Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"63\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 1 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"64\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 2 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"65\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 1 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"66\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 2 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"67\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 1 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"68\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 2 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"69\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 3 Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"70\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Dark List Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"71\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Shading Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"72\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful List Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"73\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Grid Accent 5\"/> <w:LsdException Locked=\"false\" Priority=\"60\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Shading Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"61\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light List Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"62\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Light Grid Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"63\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 1 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"64\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Shading 2 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"65\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 1 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"66\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium List 2 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"67\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 1 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"68\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 2 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"69\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Medium Grid 3 Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"70\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Dark List Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"71\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Shading Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"72\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful List Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"73\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" Name=\"Colorful Grid Accent 6\"/> <w:LsdException Locked=\"false\" Priority=\"19\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Subtle Emphasis\"/> <w:LsdException Locked=\"false\" Priority=\"21\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Intense Emphasis\"/> <w:LsdException Locked=\"false\" Priority=\"31\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Subtle Reference\"/> <w:LsdException Locked=\"false\" Priority=\"32\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Intense Reference\"/> <w:LsdException Locked=\"false\" Priority=\"33\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Book Title\"/> <w:LsdException Locked=\"false\" Priority=\"37\" Name=\"Bibliography\"/> <w:LsdException Locked=\"false\" Priority=\"39\" QFormat=\"true\" Name=\"TOC Heading\"/> </w:LatentStyles> </xml><![endif]--><!--[if gte mso 10]> <style> /* Style Definitions */ table.MsoNormalTable {mso-style-name:\"Tabla normal\"; mso-tstyle-rowband-size:0; mso-tstyle-colband-size:0; mso-style-noshow:yes; mso-style-priority:99; mso-style-qformat:yes; mso-style-parent:\"\"; mso-padding-alt:0cm 5.4pt 0cm 5.4pt; mso-para-margin:0cm; mso-para-margin-bottom:.0001pt; mso-pagination:widow-orphan; font-size:11.0pt; font-family:\"Calibri\",\"sans-serif\"; mso-ascii-font-family:Calibri; mso-ascii-theme-font:minor-latin; mso-fareast-font-family:\"Times New Roman\"; mso-fareast-theme-font:minor-fareast; mso-hansi-font-family:Calibri; mso-hansi-theme-font:minor-latin; mso-bidi-font-family:\"Times New Roman\"; mso-bidi-theme-font:minor-bidi;} </style> <![endif]--></p> <h2 style=\"text-align: center;\"> PREGUNTAS</h2> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Dicen que de la teor&iacute;a a la pr&aacute;ctica hay un abismo:</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Se ense&ntilde;a, se aprende,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Se escribe, se publica o se lo archiva.</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iexcl;Qu&eacute; dif&iacute;cil practicarla!.. &iquest;Verdad?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iexcl;Qu&eacute; alegr&iacute;a cuando nace un ni&ntilde;o!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Aunque a veces se le impide que venga a este mundo</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> o si llega a viejo, es uno dem&aacute;s en la familia.</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> ',' <p> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iexcl;Dicen que la Patria es de todos!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Mientras est&aacute; mal repartida:</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Unos la poseen mucho,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Otros un poco menos,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Otros muy poco,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Y otros&hellip; &iexcl;Totalmente nada!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Se habla de igualdad, mientras se distingue:</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Raza, color, folklor,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Raz&oacute;n social, pol&iacute;tica, econ&oacute;mica,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Religi&oacute;n, patrimonio, etc., etc.</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Los elegidos por el pueblo,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Constructores de paz, justicia y progreso de la Patria</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> No tienen paz para gobernar</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> A pesar de sus incansables ideales.</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Sabemos del que se casa y del que se divorcia,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Del que tiene y del que no posee,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Del honrado y del que roba,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Del que sabe y no sabe,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Si es de aqu&iacute; o es de all&aacute;,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> M&aacute;s&hellip; &iexcl;no sabemos de aquel que necesita!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &nbsp;</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iexcl;No quiero hacer m&aacute;s preguntas!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Pues, nadie resolver&aacute; este cuestionario,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Si busca en un archivo</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Jam&aacute;s encontraran respuesta</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Porque el escritor s&oacute;lo escribi&oacute;:</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iexcl;Tanto tienes, tanto vales,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Nada tienes, nada vales,</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> O m&aacute;s bien dicho, cada quien vale lo que pesa!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> La &uacute;nica respuesta certera es:</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> Que al final&hellip; &iexcl;Somos iguales!</p> <p class=\"MsoNormal\" style=\"text-align: center;\"> &iquest;Por qu&eacute; ser&aacute;?</p> <p class=\"MsoNormal\"> &nbsp;</p> <p class=\"MsoNormal\"> </p> <p class=\"MsoNormal\"> <strong>C&eacute;sar Gilberto Pav&oacute;n</strong></p> <p class=\"MsoNormal\"> <em>Nota: Este peque&ntilde;o poema lo escrib&iacute; bas&aacute;ndome en experiencias vividas, aunque al leerlo es duro interpretarlo. Pero &eacute;sta es la realidad de los seres humanos, m&aacute;s a&uacute;n cuando nos damos cuenta que las teor&iacute;as no son todo si no las practicamos.</em></p>','2011-01-23'),('presidente-quiere-que-se-vayan-los-gringos','Jennifer Moore','Presidente quiere que se vayan los gringos',' <p style=\"text-align: justify;\"> Amenaza de nuevo el presidente Correa a las organizaciones no gubernamentales que apoyan los movimientos sociales cuyos dirigentes cuestionan el modelo extractivista que su Gobierno promueve. En marzo de 2009, el Gobierno revocó la personería jurídica de Acción Ecológica, con sede en Quito (ver INTAG #___, página___). La medida provocó protestas nacionales e internacionales. Durante su discurso sabatino del 10 de julio, el jefe de Estado advirtió a las organizaciones que reciben apoyo internacional en estas palabras: &ldquo;Los gringuitos llegan con la barriga llena para convencerle a los indígenas que no debemos extraer el petróleo ni operar las minas. Les dan dinero, logran su fines y se van, dejando a los indígenas más pobres que nunca&rdquo;. El presidente Correa sugirió que se expulsen del país las personas que intervienen en la lucha del movimiento indígena. <br /> La sugerencia de Correa en el sentido de que el Ecuador se quedará empobrecido sin la minería, hace eco de las campañas publicitarias de las empresas mineras canadienses en el país. Por ejemplo, los lemas de Corriente Resources (vendido hace poco a una minera china), &ldquo;Trato Justo&rdquo; y &ldquo;Pobre sin Cobre&rdquo; se diseminaron en cuñas televisivas y en periódicos nacionales. La diferencia hoy es que el Presidente promueve el mayor control de los proyectos mineros por parte del Estado y la redistribución de los beneficios.</p> ',' <p>  </p> <p style=\"text-align: justify;\"> Sin embargo, las promesas dudosas de una minería responsable en términos ambientales no convencen a las comunidades indígenas y campesinas, preocupadas por los impactos de la actividad en los bosques y las fuentes de agua. O sea, creen que la minería traerá la pobreza.<br /> &ldquo;He escuchado el discurso de Correa&rdquo;, dijo Marlon Santi en una entrevista con el investigador canadiense Jeffrey R. Webber, &ldquo;en el sentido de que estamos sentados en una montaña de oro y sería estúpido no explotarlo. Pero esto es pensar en el corto plazo, sólo en el presente. Y, ¿nuestro futuro?&rdquo;</p> <p>  </p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: right;\"> <em>*Jennifer Moore es una periodista canadiense radicada en Cuenca. Ésta es la segunda parte de su artículo &ldquo;A New Wave of Criminalization Against Social Movements in Ecuador&rdquo;, publicado en la revista digital UpSideDownWorld el 14 de julio de 2010. Traducción por Mary Ellen Fieweger<br /> <br /> </em></p>','2010-09-22'),('primer-obispo-afroecuatoriano-del-pais','Los Editores','PRIMER OBISPO AFROECUATORIANO DEL PAÍS','<h2>MONSEÑOR IVÁN MINDA-PRIMER OBISPO AFROECUATORIANO DEL PAÍS</h2> <p style=\"text-align: justify;\"><strong></strong><br />Unas 1200 personas presenciaron un suceso extraordinario en la Catedral Metropolitana de Guayaquil el 28 de noviembre. En esta fecha, el padre Iván Chalá llegó a ser el primer obispo afroecuatoriano del país. Además, el Monseñor Iván Chalá es inteño, motivo de gran satisfacción para la zona.</p> <h4>La ordenación.</h4> <p><img class=\"caption\" src=\"/images/stories/portada/obispominda1.jpg\" border=\"0\" alt=\"Monseñor Minda en su llegada a Apuela, a\" title=\"Monseñor Minda en su llegada a Apuela.\" align=\"right\" style=\"border: 0; float: right;\" />El Papa Benedicto XVI nombró al padre Minda obispo auxiliar de la Arquidiócesis de Guayaquil el 4 de noviembre. Hasta dicho momento, el flamante Monseñor se desempeñaba como párroco de la parroquia de Salinas de Imbabura.  <br />Durante el rito, los presbíteros Rómulo Aguilar y Miguel Puga acompañaron al Monseñor Minda en calidad de padrinos. Uno de ellos pidió a Monseñor Antonio Arregui, presidente de la Conferencia Episcopal Ecuatoriana y Arzobispo de Guayaquil, la ordenación del nuevo obispo en nombre de la Iglesia. Después de ser aceptado para la ordenación, Monseñor Minda hizo la promesa de servir a Dios y la Iglesia. Asimismo, recibió de los obispos presentes el báculo, o bastón, como signo de su misión de buen pastor; el anillo, signo de alianza con la Iglesia; y la mitra, signo de la plenitud del sacerdocio que ha recibido. Los familiares llevaron las insignias hasta el altar y las entregaron a los obispos. El báculo leva una torre que simboliza a la Virgen María como el camino para llegar al sol y la imagen de Cristo.  <br />Por su parte, el Arzobispo Arregui dijo: “Oremos para que el Dios de todo poder y bondad derrame sobre el elegido la abundancia de su gracia. Que reciba el Evangelio y anuncie la palabra de Dios con sabiduría y perseverancia”. Una vez firmada el acta, Monseñor Arregui lo invitó a su flamante colega a sentarse en la cátedra a su derecha.</p> ',' <h4>Participación masiva de Intag</h4> <p style=\"text-align: justify;\"><img class=\"caption\" src=\"/images/stories/portada/obispominda4.jpg\" border=\"0\" title=\"Participacion masiva de inteños\" align=\"right\" style=\"float: left; border: 0;\" />En la Catedral se vivió toda una fiesta. Familiares, amigos y el coro de los misioneros afroecuatorianos del Seminario Mayor de Guayaquil lo esperaban con cantos y danzas para demostrarle su gratitud. <br />De Salinas de Imbabura, llegaron tres buses repletos de gente para acompañar a su Párroco. E Intag también demostró su orgullo: viajaron unas 250 personas de seis parroquias para estar con Monseñor Minda en su ordenación.  <br />Una vez terminada la ceremonia, a Monseñor Minda le faltaban manos para abrazar y bendecir a todos las personas que se le acercaban. Después, la mayoría de gente se trasladó al Seminario Bernardino Echeverría para servirse un almuerzo. A continuación, se ofreció un acto cultural en honor a Monseñor. El grupo del Cetro Cultural Afroecuatoriano presentó cantos y danzas con el típico ritmo de marimba mientras el músico inteño Segundo Espinoza interpretó pasillos y cánticos. <br />Durante el acto cultural, Monseñor Minda dijo sentirse feliz de haber recibido tanto cariño, tanto aprecio, tanta gracia de Dios y tanta ayuda de la gente buena de la Iglesia católica. <br />Hizo votos para que todas las expectativas que su ordenación ha despierto se cumplan con la ayuda y la misma gracia de Dios. Y añadió: “Que mi ordenación sea un signo de ese avance, ese empuje que la Iglesia necesita para evangelizar y para transformar este mundo”.</p> <h4>Monseñor Minda en Apuela</h4> <p style=\"text-align: justify;\">Llegó a Intag el nuevo prelado el 18 de diciembre. Las comunidades en la carretera hicieron arcos para mostrarle su alegría y gratitud. Además, lo esperaban con mucho entusiasmo para todas las bendiciones. <br />En Santa Rosa de Plaza Gutiérrez esperaba a Monseñor Minda el padre Nilo Cuenca con gente de la comunidad para acompañarlo hasta Apuela donde daría su primera misa en lo que se refiere a Imbabura. Lo acompañaron Monseñor Luis Figueroa, de la Diócesis de Ibarra, el padre Orlando Bustamante y otros sacerdotes que se unían a la alegría del pueblo inteño.<br /><img class=\"caption\" src=\"/images/stories/portada/obispominda2.jpg\" border=\"0\" alt=\"Monseñor Minda en Apuela\" align=\"left\" />El padre Nilo le dio la bienvenido y lo felicitó por los méritos alcanzados. También dijo que en su tierra lo esperan sus familiares y amigos. Asimismo, Oswal Erazo, presidente de la parroquia de Apuela, se unió a las felicitaciones del padre Cuenca. <br />En su intervención, Monseñor Minda dijo que Intag es un pueblo humilde y trabajador donde en medio de la pobreza reina la alegría. Pero aclaró que esto no quiere decir que se tiene que quedar con la pobreza. También, al referirse a la minería, comentó que las riquezas naturales de Intag han sembrado la desunión. Afirmó que es un tema complicado y, por eso, es necesario sentarse a dialogar para encontrar una solución que beneficie a todos. <br />Al finalizar la misa, gente de las comunidades preparó un acto cultural y un almuerzo en honor al flamante obispo.     <br />En una entrevista con el Periódico Intag, Monseñor Minda dijo que para llegar a ser obispo tiene que ver mucho la gracia de Dios y no solamente con los propios méritos de uno. “Para mí es un gran reto y a la vez una gran responsabilidad”, dijo. También explicó que para llegar a ser obispo, tiene que ser diácono y sacerdote primero. Además, aseguro que su nombramiento de Obispo Auxiliar de Guayaquil le cayó de sorpresa. Incluso pensó que se trataba de una broma o una tomadura de pelo. Cuando supo que era una designación dada por el papa Benedicto XVI, dijo que sólo le pidió a Dios que lo ayude a cumplir la doctrina de Cristo. <br />La provincia de Guayas tiene más de dos millones de personas, indicó Monseñor Minda; las diócesis con una población más de un millón tienen un obispo auxiliar para asistir al obispo. Dijo que tendrá la oportunidad de aprender más en este puesto. Asimismo, si así lo quiere Dios, un día tendrá su propia diócesis.  <br />No todos los sacerdotes llegan a ser obispos, puesto que no es como el ejército o la policía donde los miembros ascienden de categoría por los años de servicio. Depende del nombramiento del Papa y uno de los requisitos es el de tener unos 20 años de servicio. En la Iglesia depende de la voluntad de Dios, finalizó.</p> <h4>Quién es Monseñor Minda</h4> <p style=\"text-align: justify;\">Monseñor Minda Chala nació el 20 de febrero de 1960 en Cristopamba, perteneciente a la parroquia de Apuela. Estudió en la Escuela Alfonso del Hierro de Quito y en el Instituto Rosales de Ibarra. Los estudios secundarios cursó en el Colegio Nocturno Jacinto Collaguazo, de Otavalo, y el Colegio San Pablo del Lago. Al conseguir su bachillerato, ingresó en el Seminario Mayor Nuestra Señora de la Esperanza de Ibarra donde estudió filosofía. Siguió sus estudios en la Universidad de Navarra, en España, donde obtuvo la licenciatura en Filosofía.</p>','2010-03-12'),('prohibido-cometer-errores','Simon Lindenberg','Prohibido cometer errores','',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notizonacantprov/intag_subcentro.gif\" /></p> <p style=\"text-align: justify;\"> La gente está furiosa. Unos se quejan de que no les hayan atendido, otros dicen que les dieron medicamentos inapropiados y muchos coinciden en decir que el bebé murió por la culpa de los médicos. ¿Son justas estas quejas?<br /> La situación no es fácil para las y los médicos en el Sub Centro de Salud Apuela (SCS) durante estas semanas, ya que se ven confrontados con personas que muchas veces no son muy comprensivas. Las personas que integran el equipo que atiende son: la doctora Pilar Muthre, directora de Salud para la Zona; las doctoras Daniela Guevara y Mylenne Hurtado, jóvenes recién salidas de la Facultad de Medicina quienes están haciendo su año de servicio rural; el doctor Luis Cadena, quien viene de Vacas Galindo para ayudar donde sea necesario; las tres enfermeras y la enfermera auxiliar. <br /> <br /> <strong>Montón de trabajo, poco personal, ambulancia a medias</strong><br /> A pesar de que trabajan más de lo estipulado en sus contratos, hay veces que ni esto es suficiente, según la doctora Muthre. Cada día vienen entre 25 y 30 pacientes al SCS y al mes hay que atender entre 37 y 40 emergencias. La mayoría del tiempo una sola doctora atiende. Las personas que acuden al SCS quieren ser atendidas sin demora alguna. Según la doctora Muthre, muchas personas que dicen no haber sido atendidas no han esperado porque se fue el carro antes de que les tocara el turno. La doctora Daniela Guevara opina que es mejor atender bien a cada paciente antes que a medias. En otras palabras, existen dos problemas relacionados: la falta de personal por un lado y, por otro, la falta de tiempo por parte de las y los pacientes.<br /> Asimismo, hay personas que insisten que no les hayan llevado en la ambulancia al hospital de Cotacachi. Esto, aclara la doctora Muthre, sólo se hace en casos de emergencia. Añade que mucha gente no sabe qué, exactamente, es una emergencia. Las y los únicos pacientes que pueden ser tratados de emergencia son mujeres que están por dar a luz, personas intoxicadas y víctimas de accidentes. Nadie más tiene el derecho de ser trasladadas al hospital en Cotacachi en la ambulancia. Algunos vienen después de haber sufrido dolores durante unos días e insisten en que les consideren casos de emergencia. Pero no lo son, y tienen que viajar en bus o en carro privado. Otro problema es, según la doctora Muthre, que el chofer trabaje solamente cinco días por semana. Y, de vez en cuando, se ocupa la ambulancia en Cotacachi. Cuando esto ocurre, puede haber casos de emergencia que tienen que buscar otro medio para llegar al hospital. Todo esto deja inconformes a las personas afectadas. <br /> <br /> <strong>¿Quién tiene la culpa?</strong><br /> Tanto las y los doctores como las otras personas que trabajan en el SCS tienen que seguir las normas establecidas por las autoridades del hospital de Cotacachi y el Ministerio de Salud Pública (MSP). Dichas normas indican lo que se puede y no se puede realizar en un subcentro de salud como el de Apuela (ver recuadro, página___). <br /> Aparte de que el presupuesto impide que el personal realice ciertos tratamientos en el SCS, existen otros obstáculos. Existe el equipo de quirófano completo, pero no se puede operar a nadie en Apuela porque no hay un cuarto apto para instalar la maquinaria ni los técnicos que sepan manejarla. La misma situación se aplica al laboratorio: mientras existen todos los instrumentos para armarlo, faltan técnicos. Tampoco se puede hacer una ecografía o una radiografía. Y esto se debe a que lo que existe en Apuela es un Sub Centro de Salud, no más, en donde no se permiten estas actividades.  En vista de las falencias indicadas, las doctoras pueden examinar al paciente palpándole la parte que le duele, observando los síntomas visibles y escuchando lo que le explica la persona enferma en cuanto a síntomas. No existe la posibilidad de confirmar, descartar o ampliar una diagnosis mediante exámenes que dependen de la tecnología mencionada.  <br /> La doctora Muthre es la única miembro del equipo del SCS que tiene años de experiencia. Las otras médicos, como mencionamos, están haciendo su año rural. Muchas veces tienen que trabajar solos, sin el apoyo de profesionales más experimentados. La doctora Muthre afirma que, mientras sería bueno tener personal con más experiencia, sus jóvenes colegas saben distinguir la gravedad de las situaciones, y si es necesario, trasladan a las y los pacientes a Cotacachi o, en casos más graves, a Ibarra. <br /> <br /> <strong>¿Qué pasó con el bebé?</strong><br /> Sin embargo, hay situaciones imprevistas y difíciles, según constata la doctora Muthre. Y a veces, no hay cómo resolverlas. En el caso del bebé que murió un lunes de marzo en Apuela, no había cómo salvar al recién nacido: durante el parto, aspiró líquido amniótico, lo que causó la asfixia del pequeño. En ciertos casos, casi es imposible evitar que el bebé se trague el líquido. Y una vez que lo tenga en sus pulmones, es necesario trasladar al recién nacido inmediatamente a terapia intensiva, cosa que no hay en Íntag, y que no habrá durante mucho tiempo.<br /> Aunque sea difícil para mucha gente aceptar, en el caso del fallecimiento del recién nacido, no hay culpables. Las dos doctores presentes en el momento hicieron todo lo posible para reanimarlo. Su única salvación habría sido nacer en un hospital bien equipado en Quito, según la doctora Muthre.<br /> <br /> <strong>La estadística manda</strong><br /> La doctora Muthre quisiera mejorar la situación en el SCS. Pero ella no es la que manda. Mientras no haya una cierta cantidad de personas que necesitan ser operadas en Apuela, no habrá técnicos y tampoco un quirófano. Mientras no haya una cierta cantidad de personas que necesitan hacerse exámenes en Apuela, no habrá laboratorio: &ldquo;Todo depende de la estadística, esta manda todo&rdquo;, dice la directora del SCS.<br /> A pesar de las numerosas limitaciones, el SCS sigue adelante poco a poco. Hay un club de gente mayor que se reúne el último sábado de cada mes (ver página XXX), y hace poco se inauguró un servicio para mujeres embarazadas quienes pueden realiza sus chequeos en el SCS un viernes por mes. Las muestras se llevan a Cotacachi y el lunes están los resultados en Apuela.<br /> En cuanto a las quejas de la población inteña, la directora Muthre dice que todas y todos los empleados del SCS Apuela hacen el mayor esfuerzo para hacer un buen trabajo, el personal ha tenido la formación necesaria para los desafíos en el Sub Centro. <br /> Pero de igual manera, por más doctora que sea una profesional de la Salud, sigue siendo un ser humano, como todos los demás. <br /> Normal 0 21 false false false ES X-NONE X-NONE /* Style Definitions */ table.MsoNormalTable {mso-style-name:\"Tabla normal\"; mso-tstyle-rowband-size:0; mso-tstyle-colband-size:0; mso-style-noshow:yes; mso-style-priority:99; mso-style-qformat:yes; mso-style-parent:\"\"; mso-padding-alt:0cm 5.4pt 0cm 5.4pt; mso-para-margin:0cm; mso-para-margin-bottom:.0001pt; mso-pagination:widow-orphan; font-size:11.0pt; font-family:\"Calibri\",\"sans-serif\"; mso-ascii-font-family:Calibri; mso-ascii-theme-font:minor-latin; mso-fareast-font-family:\"Times New Roman\"; mso-fareast-theme-font:minor-fareast; mso-hansi-font-family:Calibri; mso-hansi-theme-font:minor-latin; mso-bidi-font-family:\"Times New Roman\"; mso-bidi-theme-font:minor-bidi;} </p> <p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt;\"> </p> <p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt;\"> <strong>Una reflexión:<br /> </strong></p> <p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: center;\"> <span style=\"color:#006400;\"><span style=\"font-size: 14px;\"><strong>Sobre mini hospitales, presidentes, errores y sabios<br /> </strong></span></span></p> <h6> Mary Ellen Fieweger</h6> <p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt; text-align: justify;\"> ¿Y dónde está el mini hospital que nos prometió para la Zona? Durante los tres períodos del ex alcalde Auki Tituaña se avanzó bastante en el tema: se construyó el nuevo edificio (donde funciona el Subcentro de Apuela), se gestionaron los equipos necesarios para subir el Subcentro a la categoría de Centro de Salud &ndash;o minihospital&ndash; y llegaron a trabajar en la Zona médicos cubanos, así llenando bastante el vacío en cuanto a personal preparado. Todo esto dentro de la descentralización de la salud y otros servicios en marcha durante ese tiempo. <br /> Entonces, ¿qué pasó? Lo que pasó es Rafael Correa. Al llegar a la presidencia, el actual jefe de Estado y sus huestes iniciaron la re-centralización de servicios básicos, entre estos, la salud y la educación. Como consecuencia, el alcalde actual, incluso si tuviera el afán de dotar a la Zona con un abanico lo más amplio posible de servicios de salud &ndash;y no existe razón para pensar que no tenga dicha voluntad&ndash; no podría en vista de las reglas de juego impuestas por el Gobierno actual. <br /> Como resultado de estas reglas, la salud en el país es un sector de contrastes pronunciados. Por un lado, el Gobierno ha inyectado considerables recursos en el sector. Y, en teoría, &ldquo;la salud ya es de todos&rdquo;. Pero solo en teoría. A pesar de la emergencia declarada hace tiempos en el sector, la situación en muchos lugares es lamentable: la demanda excede la oferta, en términos tanto de personal como de infraestructura e insumos. Cada día salen en los noticieros y en las conversaciones entre vecinos, colegas, comadres, familias anécdotas como éstas: que no existen insumos tan básicos como aspirina en tal o cual hospital, que es imposible conseguir una cita para consultar a un médico en el hospital del IESS, que un pobre mayor ha tenido que esperar más de un año para que le operara la próstata por falta de una pieza para un equipo que ya no se fabrica pero que en el sistema de salud estatal del Ecuador se sigue utilizando. <br /> Tal vez lo más desgarrador es la situación de los pacientes más indefensos: los recién nacidos. En este tema ha habido una verdadera tormenta de malas noticias en los últimos tiempos. Hace unos meses se denunció que en el hospital de Santo Domingo de los Tsáchilas, los bebés eran llevados de la sala de parto a descansar en cajas de cartón o, en el mejor de los casos, en lavacaras de plástico. Poco después, se denunció que en el hospital de Ambato, las cunas tienen más de medio siglo y ya no funciona el aparato que debería entregar oxígeno a los pequeños seres con deficiencias respiratorias. Más tétrico aún es la tasa de mortandad de infantes en algunos centros de salud a cargo del Estado. El caso más reciente es el de Guayaquil, donde en febrero murieron por lo menos cinco bebés en pocos días ya no debido a la falta de equipos sofisticados o muebles esenciales o medicamentos básicos, sino por crasos errores humanos y una irresponsabilidad espeluznante en temas tan básicos como la limpieza.<br /> Pero incluso en los centros de salud equipados con tecnología de punta, con profesionales con larga experiencia graduados de las facultades de medicina más exigentes del mundo, se cometen errores. Un estudio hecho hace unos cinco años en los Estados Unidos señala que en los hospitales de todo el país los errores evitables cometidos por profesionales causan la muerte de casi dos cientos mil pacientes por año. <br /> Para el Ecuador no existen cifras al respecto. Lo que no quiere decir que no haya muertos por errores evitables. De hecho, y como dice el dicho: &ldquo;Errar es humano.&rdquo; La segunda, y más importante, parte del dicho reza así: &ldquo;Corregir es de sabios&rdquo;. Pero para ser sabio, hay que aprender de los errores. Y para aprender de ellos, primero hay que reconocerlos, una virtud que nos hace muchísima falta aquí en la Zona, en todo ámbito.</p> <p class=\"MsoNormal\" style=\"margin-bottom: 0.0001pt;\"> <br />  </p>','2011-06-07'),('protagonistas-de-cambio','Los Editores','protagonistas de cambio','','<p><strong>QUE NO SE LES SUBAN LOS HUMOS</strong></p> <h2>QUE SEAMOS TODOS/AS PROTAGONISTAS EN EL CAMBIO</h2> <p style=\"text-align: justify;\"><br />“El poder del pueblo sobre sus mandantes”. Esta frase la escuchamos todos los sábados cuando sintonizamos la cadena del presidente Rafael Correa. Además, los y las funcionarias del Gobierno la repiten con frecuencia en época de Revolución Ciudadana. ¿Sería que la sociedad civil tiene realmente poder en estos tiempos? ¿Participamos en la toma decisiones estatales? ¿Cómo están manejando el poder, tanto el Gobierno como la sociedad civil? En Íntag ¿será que estamos replicando el modelo de tener líderes sabelotodo que creen que pueden decidir a su antojo? O, ¿las decisiones sí se toman en consenso? Algún gran amigo de la zona dijo que acá hay una “reunionitis, asambleitis y talleritis intensa”. ¿Estos espacios sirven para tomar decisiones y planificar de acuerdo a la mayoría? O pasa igual que con el “show de la participación ciudadana” del Gobierno en donde las decisiones siguen siendo tomadas por unos cuantos iluminados. ¿Quién tiene el poder?<br />En un sentido amplio, el poder es la habilidad que tienen ciertos grupos o personas de imponer la voluntad. Es decir, es la habilidad para influir sobre la conducta de otros. El poder se puede ejercer a todo nivel. Lo que hacen los jefes de estado, legisladores y jueces es un tipo de poder. Pero también influyen en la conducta de otros, en sus grandes o pequeños espacios, los líderes de organizaciones, profesores y jefes de hogar.<br />Cuando una persona o grupo controla las decisiones y acapara tanto poder se distancia tanto de los demás que se va quedando sólo. Pero es una soledad cuyo precio muchos están dispuestos a pagar. El poder atrae a tantos porque trae tantos beneficios. En algunos casos, los beneficios son económicos pero no siempre legítimamente obtenidos. En todos los casos, los beneficios también son sicológicos. Expertos en el campo de la psicología afirman que el poder es como un narcótico, una sustancia que produce dependencia o adicción, algo sin la cual el adicto no puede vivir. También dicen –y esto es medio irónico– que la gente que no puede vivir sin controlar a sus semejantes son personas super inseguras, necesitan que los demás les están afirmando su importancia de manera permanente.<br />Pero los que más sufren son las personas que tienen que lidiar con el ansioso de controlar, a si sea en el hogar, en una organización o en un país. Cuando una persona o un grupo abarca el poder, provoca desigualdad. No reconocer la importancia que tienen los demás y creerse indispensable conlleva a decisiones tomadas de forma unilateral, es decir, sin consultar al resto. Mientras esto es injusto para el poderosos puesto que sobre sus hombros cae todo el peso de la responsabilidad, más injusto es el resto de la gente.  Pero más peligroso aún es que cuando nos sentimos tan necesarios, no creemos necesario rendirle cuentas a nadie y tampoco admitimos que nos cuestionen o critiquen.<br />Así se va creando un ambiente de miedo o recelo. Nadie se atreve a preguntar sobre las más obvias irregularidades porque hay cosas de las que simplemente ya no se habla. ¿Esto es democracia? Escuchamos con frecuencia que los asambleistas son alza manos. A nivel familiar u organizativo, ¿somos alza manos, o sí, cuestionamos, elegimos democráticamente, participamos y pedimos aclaraciones? <br />En nuestras organizaciones, el tener directivas o líderes permanentes hace que el ejercicio del poder se concentre en pocas manos. Las directivas eternas se vuelven intocables también, porque otra gente no quiere asumir la responsabilidad. Sin duda, esto se debe a la calidad de educación que se ofrece en las escuelas, la familia y la organización, o sea, su inhabilidad de formar personas dispuestas a asumir responsabilidades, de consultar con otros, de respetar la opinión ajena, de admitir que pueden no tener todas las respuestas, que también es capaz de cometer errores y que por eso necesita a sus compañeros y compañeras para ayudar en la toma de decisiones. Cuando un país se rige por un líder permanente, se llamada dictadura. Cuando una organización se rige por directivas o líderes permanentes, ya no es una organización de base sino una fundación. Las fundaciones no tiene nada de malo: son creadas por iniciativa particular para atender, sin ánimo de lucro, servicios de interés social y se rigen por la voluntad de sus fundadores, por sus estatutos y por la Ley. Pero no son organizaciones en donde las personas que las integran son capaces de comunicarse y que están dispuestas a actuar en conjunto para obtener un objetivo común. Son un grupo social que tiene propósitos específicos que son acordados por la mayoría de sus miembros mientras las decisiones son tomadas de manera democrática por sus miembros quienes tienen acceso a toda la información necesario para hacerlo. O sea, la organización de base se caracteriza por su actitud democrática, su voluntad de compartir conocimientos para involucrar a todo el mundo en la toma de decisiones y su transparencia. <br />Para construir una sociedad mejor y, sobre todo, más democrática, en las esferas de la familia, las organizaciones y las instancias públicas, nuestras actitudes tienen que cambiar. No bastan las buenas intenciones, las ideas fantásticas o la preparación maravillosa. Tenemos que aprender a compartir información, confiar en la inteligencia de nuestras compañeras y compañeros, aceptar que somos capaces de cometer errores, pedir perdón. En otras palabras, todos y todas tenemos que convertirnos en protagonistas del cambio, con opción a opinar, criticar, participar y disentir.</p>','2010-06-17'),('protestan-contra-lafarge','Nora J. Sinner','Protestan contra Lafarge','',' <p style=\"text-align: center;\"> <img alt=\"alt\" src=\"/images/stories/notizonacantprov/lafarge.jpg\" style=\"width: 369px; height: 123px;\" /></p> <p style=\"text-align: justify;\"> <br /> No se escuchaba el acostumbrado ruido ensordecedor de las maquinas en la planta industrial de la empresa internacional Lafarge en la vía Otavalo-Selva Alegre el 24 de enero, un lunes. ¿La razón? Los moradores de las comunidades aledañas cerraron las puertas con cadenas y candados. Ese día la producción se paralizó. Decenas de moradores de las comunidades vecinas protestaron, con palos afilados, contra la contaminación del medio ambiente que ha producido la compañía y los impactos en la salud de la población. En la protesta participaron también inteños de El Quinde, La Libertad y Quinde Talacos, comunidades ubicadas en las cercanías de las minas de caliza de donde Lafarge saca la materia prima que se convierte en cemento. <br /> Las afectaciones denunciadas por los moradores incluyen impactos en el río Quinde. Los moradores que usan el agua de esta fuente dicen que está contaminada con lodo, piedras, aceites y aguas servidas. <br /> Geovanny Guevara, una de las personas inconformes con la forma de actuar de la empresa, lamentó que ya &ldquo;estamos reclamando desde hace cuatro años. Vivimos en medio de una nube de polvo que afecta nuestra salud y nuestros cultivos&rdquo;. <br /> De la misma forma, José Perugachi, de la comunidad de El Corazón responsabilizó a Lafarge la pérdida de seis vacas que murieron por comer hierba de un pasto cubierto de cemento. <br /> La mayoría de manifestantes no exigió el cierre de la planta y las minas sino que se tomen medidas de mitigación de los impactos y remediación de los daños.<br /> <br /> <strong>Los acontecimientos</strong><br /> A las ocho de la mañana llegó la policía. Arrestaron a cinco personas y unas horas luego de su cierre, lograron abrir las puertas de la planta. Según la comisaria de Otavalo, Marianita Montalvo, el viernes anterior los manifestantes ya intentaron tomar la vía de Otavalo a Selva Alegre y luego obstaculizaron la planta. Además, aseguró la comisaria que durante la mañana del bloqueo, los manifestantes trataron de secuestrar al jefe de seguridad de la cementera, José Cazares. Según la autoridad, la empresa ya denunció a los detenidos en la Fiscalía de Otavalo. <br /> Cerca del mediodía, la asambleísta Marisol Peñafiel y el gobernador de Imbabura, Pedro Dávila, se ofrecieron como mediadores del conflicto. La asambleísta Peñafiel constató que Lafarge sí tiene que actuar. Según ella, el Ministerio del Medio Ambiente ya pidió hace un año que la empresa presentara un plan de manejo ambiental. Además, el Ministerio realizó 16 observaciones de la planta. La idea fue encontrar soluciones a los problemas denunciados por los moradores. El 22 de diciembre Lafarge presentó una auditoria ambiental. Ahora una comisión interinstitucional está analizando la investigación.<br /> En el próximo número del Periódico ÍNTAG reportaremos en detalle sobre los problemas expuestos por los manifestantes y la reacción de la empresa Lafarge.</p> <p style=\"text-align: right;\"> <em>Fuentes: &ldquo;Las protestas contra Lafarge paralizaron la producción de cemento&rdquo;, El Comercio, 25 de enero de 2011, http://www4.elcomercio.com/Negocios/las_protestas_contra__lafarge__paralizaron_la____produccion__de_cemento.aspx; &ldquo;Comuneros radicalizaron medida&rdquo;, La Hora, 25 de enero de 2011, http://www.lahora.com.ec/index.php/noticias/show/1101084278/-1/Comuneros_radicalizaron_medida.html.<br /> <br /> <br /> </em></p>','2011-05-06'),('pucara-la-obra-mas-visitada','Mary Ellen Fieweger','Pucará: la obra más visitada',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notiparroqcomuni/intag_pucara2.gif\" /></p> <p style=\"text-align: justify;\"> Lo penoso es lo siguiente: que el pueblo japonés, mediante su Embajada en Quito, haya invertido casi 80 mil dólares en una obra que ya es un grave problema sanitario para la comunidad de Pucará. Esta verdad fue constatada, una vez más, por el director de Obras Públicas del Municipio de Cotacachi. El ingeniero Joel Haro hizo esta nueva inspección &ndash;la séptima&ndash; del sistema el ____ de noviembre (para un resumen de las seis inspecciones anteriores, ver recuadro, página ____). Lo acompañaron el presidente del cabildo, Marcelo Lalama; Hugo Montenegro, vocal de la Junta Parroquial de Apuela; la doctora Karen Knee, experta en estudios medioambientales y geológicos; y moradores de Pucará.</p> ',' <p>  </p> <p style=\"text-align: justify;\"> Entre los grandes ausentes estaban la doctora Carmen Trujillo y el licenciado Rolando Lomas, dirigentes de la Fundación Ecuatoriana de Desarrollo Social (FUNEDES), entidad que gestionó y ejecutó la obra hace tres años. Tampoco acudió un representante de la Embajada del Japón.  <br /> <br /> <strong>Todo el mundo está de acuerdo: no funciona</strong><br /> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"230\" hspace=\"8\" src=\"/images/stories/notiparroqcomuni/intag_pucara.gif\" style=\"width: 247px; height: 230px;\" width=\"247\" />El ingeniero Haro escuchó una presentación de los antecedentes del proyecto. El licenciado Lalama le informó que mientras el ingeniero _____ Morejón firmó un contrato para 27 mil dólares con la FUNEDES para instalar el alcantarillado, la Embajada japonesa le entregó un total de $77.597,91 a la Fundación. Con el respaldo de un video basado en la filmación que hizo el jefe del cabildo durante algunas de las inspecciones anteriores, el licenciado Lalama le indicó al ingeniero Haro las fallas en el sistema: tubería a la vista, fugas en múltiples puntos, el tanque lleno de desechos sólidos a pesar de que solo cuatro familias estaban conectadas al sistema en ese momento, y así por el estilo. Asimismo, el contrato contiene rubros nunca ejecutados, por ejemplo, el estudio de impactos ambientales y el sistema de filtración. Los moradores también le explicaron al ingeniero que los personeros de la FUNEDES convocaron a una reunión para entregar la obra. No invitaron al cabildo y el Acta de Entrega firmada durante la reunión incluye firmas de menores de edad y personas que no viven en Pucará. <br /> Luego, el ingeniero recorrió varios tramos de la tubería y emitió observaciones. Afirmó que los tubos deben estar enterrados a un metro de profundidad si hay tráfico vehicular y, caso contrario, a 80 centímetros, pero en ningún caso deben estar en la superficie. Destapó una de las cañerías en el tramo de una vecina cuya casa está entre las seis conectadas al sistema. En ese instante, la señora se encontraba lavando ropa. El ingeniero constató que el agua de la lavandería no fluía por la tubería y sentenció que se había colapsado el sistema en dicho tramo. <br /> Luego de la inspección, la doctora Karen Knee, científica especializada en estudios geológicos y medioambientales, presentó los resultados de los análisis de muestras de agua que tomó de tres sitios del río Toabunche: cerca de donde entran al río las aguas servidas del sistema de alcantarillado, más aguas arriba y aguas debajo de este punto. En la muestra del primer punto, la científica encontró una cantidad alarmante de E coli, una bacteria presente en las heces del ser humano mientras la muestra tomada aguas arriba tenía un índice normal para esta bacteria.  <br /> Además, los participantes señalaron irregularidades y ausencias en los documentos, entre otras, una serie de rubros duplicados en el presupuesto y 1,5 mil dólares para un estudio de impacto ambiental nunca elaborado. Por su parte, el ingeniero indicó que la FUNEDES debería entregar facturas, pruebas y otros respaldos con un informe económico. Afirmó que una obra de esta naturaleza no debería costar más que 47 mil dólares en 2010. Finalmente, dijo que mientras sólo la Embajada de Japón puede demandar a la FUNEDES, el Municipio hará el seguimiento.<br /> <br /> <strong>Lo que dice la Embajada de Japón</strong><br /> <img align=\"right\" alt=\"alt\" border=\"1\" height=\"182\" hspace=\"8\" src=\"/images/stories/notiparroqcomuni/intag_pucara3.gif\" style=\"width: 243px; height: 182px;\" width=\"243\" />En mayo del año pasado, Francisco Grijalva, promotor ambiental del Municipio de Cotacachi acompañado por una técnica del Ministerio de Desarrollo Urbano y Vivienda, hizo una inspección del alcantarillado &ndash;la sexta&ndash; a pedido del licenciado Lalama. Recién el 12 de octubre el técnico envió los resultados de la inspección al ingeniero Diego Guitarra, jefe de la Dirección de Biodiversidad, Tierra y Agua del Municipio (D.B.T.A.). Este documento, INFORME-061-GMC-SSA.2010, termina con la siguiente recomendación: &ldquo;Al no cumplir con los requisitos técnicos de construcción este sistema de alcantarillado sanitario, no debería prestar servicio por los problemas ambientales y de salud que puede causar a la población del sector&rdquo;. <br /> El 25 de octubre, el ingeniero Guitarra se dirige al alcalde Alberto Anrango mediante el oficio No. 100 D.B.T.A. en el cual le solicita al burgomaestre que &ldquo;se digne realizar los trámites correspondientes en la Embajada de JAPON, y la Fundación FUNEDES para que se haga las correcciones necesarias en el mencionado sistema&rdquo;.<br /> El alcalde, por su parte, se dirige en el Oficio No. 1017 G.M.C.A.-2010 al embajador del Japón en Ecuador, Osamo Imay, el 4 de noviembre. A este documento adjunta el informe mencionado y le dice lo siguiente al diplomático japonés: &ldquo;En este sentido me permito solicitar de la manera más comedida a la Fundación FUNEDES, la readecuación del sistema de alcantarillado sanitario, para que con ello se cumpla el objetivo con el que fue construido&rdquo;. <br /> Shinji Nishiyama, encargado de Cooperación Técnica y Financiera de la Embajada de Japón, respondió al alcalde Anrango en el oficio No. 107-G el 8 de noviembre. En este documento, afirma lo siguiente: que FUNEDES &ldquo;no ha entregado los documentos sobre la entrega provisional ni definitiva de la obra debidamente fiscalizada como era su obligación&rdquo;. Como consecuencia, la Embajada se dirigió a la Contraloría General del Estado dos veces para solicitar que esta instancia haga la fiscalización de la obra, &ldquo;sin que hasta la fecha se haya hecho realidad dicha fiscalización&rdquo;. Además, la Embajada &ldquo;se ha dirigido [en] varias ocasiones al señor [Rolando] Lomas, principal de Funedes, sin lograr que el mismo solucione el problema que había causado&rdquo;. En vista del silencio del licenciado Lomas, ex supervisor de Educación de la zona de Íntag, el 21 de octubre la Embajada le envió a la Fundación una carta &ldquo;en la que le conmina a entregar la obra debidamente terminada y fiscalizada o la devolución de la totalidad del dinero entregado por la Embajada&rdquo;. Ni el licenciado Lomas ni nadie más de la FUNEDES da señales de vida. En vista de estos pasos y sus resultados, la Embajada alega haber &ldquo;agotado todas las instancias&rdquo;. Afirma que &ldquo;los daños que se causare son de absoluta responsabilidad de Funedes, puesto que la Embajada no ha recibido oficialmente la obra y por lo tanto tampoco ha entregado a la comunidad&rdquo;. Concluye el señor Shinji Nichiyama que el Municipio puede iniciar &ldquo;acciones legales pertinentes en contra de Funedes por el incumplimiento anotado, puesto que la donación al nacionalizarse permite la intervención de organismos como el suyo&rdquo;. Y el funcionario agradece la intervención del Municipio &ldquo;en este penoso asunto&rdquo; y ofrece toda la documentación necesaria.<br /> El 2 de diciembre conversamos con el doctor _______ Pazmiño, funcionario de la Embajada de Japón. Confirmó los puntos señalados anteriormente y le acusó a Rolando Lomas de haber incurrido en &ldquo;malversación de fondos&rdquo; al construir el cerramiento de la escuela y regalar &ldquo;como Papa Noel&rdquo; inodoros y tubos de acometido a los moradores de la comunidad, elementos que no constan en el convenio con la Embajada. Pero insiste en que sólo el Municipio pueda demandar a la Fundación, e indica que &ldquo;si pudiéramos demandarla, lo habríamos hecho&rdquo; y es por esta situación que la Embajada se ha ido a la Contraloría.<br /> <br /> <strong>Municipio tomará cartas&hellip;</strong><br /> En una conversación telefónica con el ingeniero Haro el 20 de diciembre, el funcionario municipal nos explicó que &ldquo;hemos estado buscando a los ejecutores de la obra. No aparecen&rdquo;. Indicó, además, que &ldquo;la Embajada no puede recibir la obra ya que está mal ejecutada&rdquo;. Finalmente, dijo que el fin de año es un momento complicado en el Municipio porque los funcionarios tienen una multiplicidad de tareas que cumplir y que no ha tenido el tiempo debido para concentrarse en el problema de Pucará. Pero se compromete a hacerlo en 2011.<br /> El 14 de diciembre de 2010 se cumplió el tercer aniversario de la firma del convenio para la instalación de una obra que se ha convertido en una amenaza para la salud de la comunidad de Pucará. Hacemos votos para que el Municipio encuentre una solución y que, al finalizar este nuevo año, no tengamos que seguir reportando sobre lo que la Embajada de Japón denomina &ldquo;este penoso asunto&rdquo;.<br /> <br /> </p> <p>  </p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: center;\"> <span style=\"color: rgb(0, 128, 0);\"><span style=\"font-size: 18px;\"><strong>LAS INSPECCIONES ANTERIORES<br /> </strong></span></span></p> <p style=\"text-align: justify;\"> 10 de abril de 2008: La primera inspección de la obra de alcantarillado en Pucará la llevó a cabo la ingeniera Sandra Vaca, funcionaria del Municipio. Entre otras anomalías, constató que se instalaron al revés los tubos, que se puso macilla en la parte superior de los tubos pero no en la parte inferior desde donde se producirían fugas, que no había planillas de pago&hellip; En fin, que no había adecuada dirección técnica. El Municipio le dio a Rolando Lomas, director ejecutivo de FUNEDES y, en esa época, supervisor para la Dirección Provincial del Ministerio de Educación a cargo de la zona de Íntag, diez días para entregar al Municipio todos los planes, especificaciones, contratos y convenios relacionados con la obra, algo que debería haber hecho ANTES de iniciar la obra a fin de conseguir los permisos requeridos. ¿Cumplió? No. <br /> <em>(Fuente: &ldquo;Municipio suspende obra&rdquo;, Periódico ÍNTAG, #53, mayo-junio de 2008, página 17)</em><br /> <br /> 2 de septiembre de 2008: Esta es la fecha del informe emitido por el ingeniero Fernando Andrade, jefe de Obras Públicas para el Municipio, a cargo de la segunda inspección, llevado a cabo en días anteriores. Indicó el funcionario que con los 79 mil presupuestados para la obra se podría haber construido TRES sistemas iguales. Pero que no necesitaba ni uno porque antes de instalar este sistema, NO HABÍA PROBLEMA SANITARIO en Pucará. Indicó, además, que no existía estudio de impactos ambientales y que los planes incluían diez problemas técnicos de consideración. E incluyó la siguiente observación en su informe: &ldquo;&hellip;no es justo que, lejos de ser una solución, [la obra] se haya constituido en un grave problema de contaminación del medio ambiente de la comunidad&rdquo;.<br /> <em>(Fuente: &ldquo;Lejos de ser una solución&rdquo;, Periódico ÍNTAG, #55, septiembre-octubre de 2008, página 8)</em><br /> <br /> 11 de noviembre de 2008: Funcionarios de la Embajada acompañan al ingeniero Andrade en una nueva inspección. Entre otras cosas, confirman que 15 de los 16 tramos del sistema son mal alineados, que un tramo está por encima de la tierra, que no hay sistema de filtración, y mucho, muchísimo más.<br /> <em>(Fuente: &ldquo;Contraloría intervendrá en Pucará&rdquo;, Periódico ÍNTAG, #57, enero-febrero de 2009, página 11)</em><br /> <br /> 28 de septiembre de 2009: Osval Erazo, presidente de la Junta Parroquial de Apuela, y Marcelo Lalama, presidente de Pucará, inspeccionan la obra. El agricultor Nelson Vetancourt denuncia que su proyecto de pollos ha sido afectado por las aguas servidas que llega sin tratamiento al río Toabunchi y tiene que invertir en 800 metros de manguera a fin de coger agua limpia de otro sitio. El personero parroquial promete fiscalizar la obra. Hasta la fecha, no lo ha hecho, pero sí ha sugerido que la comunidad reciba la obra, según el licenciado Lalama.<br /> <em>(Fuente: &ldquo;Junta Parroquial fiscalizará el alcantarillado de Pucará&rdquo;, Periódico ÍNTAG, #62, noviembre-diciembre de 2009, página 13)</em><br /> <br /> 11 de mayo de 2010: El promotor ambiental del Municipio Francisco Grijalva inspecciona la obra con una técnica del MIDUVI. Recomienda que &ldquo;al no cumplir con los requisitos técnicos&hellip;no debería prestar servicio por los problemas ambientales y de salud que puede causar&hellip;&rdquo;.<br /> <em>(Fuente: INFORME-061-GMC-SSA.2010, 12 de octubre de 2010, dirigido al ingeniero Diego Guitarra, director de Biodiversidad, Tierra y Agua del Municipio de Cotacachi, firmado por el promotor ambiental Francisco Grijalva)</em>  <br /> <br /> 27 de agosto de 2010: Pablo Almeida, inspector del Ministerio de Salud para la zona de Íntag, emite un informe en que consta que &ldquo;la red de alcantarillado es una fuente constante de contaminación&rdquo; y que &ldquo;están próximas a rebosar las aguas servidas en el pozo séptico&rdquo;.<br /> <em>(Fuente: &ldquo;Pucará requiere soluciones urgentes&rdquo;, Periódico ÍNTAG, #67, septiembre-octubre de 2010, página 18)</em><br /> </p> <p style=\"text-align: justify;\"> <br /> <br /> <br /> </p> <p style=\"text-align: center;\"> <span style=\"font-size: 18px;\"><strong> El alcantarillado contamina el río Toabunchi</strong><br /> </span></p> <p style=\"text-align: justify;\"> A continuación, los resultados del análisis de las muestras de agua tomadas de los tanques del sistema de agua de Pucará y de la quebrada a donde llegan las aguas servidas del sistema de alcantarillada. Las muestras fueron analizadas por la doctora Karen Knee, especialista en estudios geológicos y medioambientales con su doctorado de la prestigiosa Universidad de Stanford, Estados Unidos. La doctora Knee elaboró la explicación que sigue el cuadro de resultados; compara lo que encontró en Pucará con los resultados de análisis que ha hecho de muestras tomadas de otros sitios de la zona.<br /> <br /> <strong>Muestra                           pH             Turbidez (cm)          Coliformes Totales (bacteria/100 mL)     E. coli (bacteria/100 mL)</strong><br /> Tanque 1                         6                     100                                       260                                                                0<br /> Tanque 2                         6                     100                                     3020                                                              20<br /> Tanque de Reserva      6                     100                                       680                                                                 0<br /> Quebrada                     5.5                      61                                   11400                                                              810<br />  <br /> <strong>¿Qué quieren decir estos datos?</strong><br /> &bull;    pH: Estos valores son típicos de todas las muestras que he analizado, inclusive de ríos en bosques protectores. Parece que el pH natural y saludable de los ríos de Intag es alrededor de 6, que es un poquito más ácido del agua completamente pura del laboratorio (pH = 7). Es posible que el agua de la quebrada tiene un pH un poquito más ácido por la contaminación, pero la diferencia es pequeña. <br /> &bull;    Turbidez: Si este valor es 100 cm, quiere decir que el agua es clara. Todas las muestras que he tomado tienen una turbidez de 100 cm, excepto la de la quebrada en Pucará donde el agua es menos clara. Esta baja claridad puede ser una indicación de contaminación. <br /> &bull;    Coliformes totales: Los índices de coliformes totales en los tanques son típicos de los de otras muestras que he tomado en diferentes lugares de la zona de Ìntag (vertientes, sistemas de agua potable, ríos en reservas y áreas agrícolas). Puede indicar cierto nivel de contaminación, quizás de heces de animales silvestres o domésticos. La presencia de estas bacterias indica que es preferible hervir, filtrar o clorar el agua, porque si hay coliformes también puede haber bacterias patogénicas (o sea, que causan enfermedades). La concentración de coliformes totales en la quebrada es mucho más alta, lo que puede indicar contaminación. <br /> &bull;    E. coli: La ausencia de estas bacterias en los tanques 1 y de reserva es una buena señal. Su presencia en el tanque 2 puede indicar un problema con contaminación, pero es muy baja. En cambio, la concentración de estas bacterias en la quebrada es muy alta y probablemente indica un problema grave de contaminación. Estos resultados indican que vale hervir, filtrar o clorar el agua antes de tomarla, y que el sistema de alcantarillado está aportando cantidades alarmantes de bacteria a la quebrada y probablemente al río Toabunchi. <br />  <br /> <br /> <br /> <em><strong>Pies de fotos:</strong><br /> El ingeniero Haro conversa con la señora       sobre los problemas debidos al sistema de alcantarillado en Pucará. (Foto: Mary Ellen Fieweger)<br /> <br /> El ingeniero Haro (centro) con Marcelo Lalama, presidente de la comunidad de Pucará, y Hugo Montenegro, vocal de la Junta Parroquial de Apuela. (Foto: Mary Ellen Fieweger)<br /> </em></p>','2011-01-19'),('rana-mas-pequena-de-ecuador','Los Editores','Rana más pequeña de Ecuador','<p> <strong>EN LA CORDILLERA DEL CÓNDOR </strong></p> <p style=\"text-align: center;\"> <span style=\"color: rgb(0, 100, 0);\"><strong><span style=\"font-size: 22px;\">DESCUBREN LA RANA MÁS PEQUEÑA DEL ECUADOR<br /> </span></strong></span></p> <p> * Carolina Carrión</p> <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" hspace=\"1\" src=\"/images/stories/notipaismundo/intag.sapos1.jpg\" /></p> <p style=\"text-align: justify;\"> <br /> <br /> &ldquo;Imagínate, un vertebrado que cabe en la uña de tu pulgar, y que se está reproduciendo&rdquo;. Investigadores ecuatorianos descubrieron a una diminuta rana cerca de la frontera con el Perú. Al igual que Íntag, esa zona es muy biodiversa pero se encuentra acechada por intereses extractivistas. Para conocer más sobre el hallazgo, y saber si las actividades mineras afectarían sus investigaciones, entrevistamos al doctor Juan Manuel Guayasamín, uno de los descubridores.<br /> El científico nos indicó que fue recién al cuarto día de trabajo de campo que se dieron cuenta que habían encontrado una nueva especie. La bautizaron Pristimantis minimus, el apellido debido a su tamaño tan mínimo. Los investigadores de la Universidad Católica encontraron 50 individuos en las partes altas de las montañas australes. Lo asombroso de esta especie es que, a pesar de su tamaño, no se tratan de ranas infantiles. &ldquo;Estos pequeñines ya tenían el tamaño y la edad para reproducirse&rdquo;, afirmó el biólogo.  <br /> El vertebrado tiene vientre traslúcido y puede medir hasta dos centímetros en edad adulta. La rana minúscula de las montañas es endémica de las zonas más húmedas del bosque de la Cordillera del Cóndor, indicó el experto en Evolución. Por otro lado, el doctor Guayasamín recalcó que en la investigación participaron profesionales del Museo de Zoología de la Universidad Católica del Ecuador, del Herbario de Loja y de las Universidades de Louisiana y de Illinois en los Estados Unidos. Aclaró que el trabajo de campo lo realizaron con la coordinación de Conservación Internacional-Ecuador, la Fundación Arcoiris y la comunidad de San Miguel de las Orquídeas.</p> ',' <p>  </p> <p style=\"text-align: justify;\"> <br /> <strong>El Ecuador tiene su mejor recurso en la naturaleza</strong><br /> &ldquo;Vivir en el Ecuador es, en verdad, un privilegio&rdquo;, dijo. &ldquo;Estamos rodeados por una diversidad inmensa. Es muy probable que, por unidad de área, el Ecuador sea el país que más especies de animales y plantas concentra&rdquo;. Esto, sin embargo, también es una gran responsabilidad, aclaró. La diversidad del Ecuador está bajo el cuidado de todos, recalca el científico. Nos recuerda que la Cordillera del Cóndor y la zona de Íntag tienen condiciones similares: altos niveles de biodiversidad combinados con una elevada presión económica ligada a la minería. &ldquo;Esto provoca intereses encontrados en donde, normalmente, la que sale perdiendo es la biodiversidad y, muchas veces, las comunidades locales&rdquo;. <br /> Asimismo asegura que en un país como el nuestro siempre se ha tendido a favorecer la exploración y la explotación en desmedro de la naturaleza. La lógica con la que se trabaja es que los recursos no renovables, como el petróleo y los minerales, deben ser extraídos para que el Ecuador pueda convertirse en un país desarrollado. Este argumento, obviamente, no es del todo cierto, aseveró. <br /> <img align=\"right\" alt=\"alt\" border=\"1\" hspace=\"8\" src=\"/images/stories/notipaismundo/intag.sapos-2.jpg\" style=\"width: 225px; height: 215px;\" />Enfáticamente, el doctor asegura que cualquier compañía o persona que diga que es posible recuperar un ecosistema de la minería a cielo abierto esta mintiendo. La complejidad biológica es irrepetible y no se puede recuperar sembrando unos árboles aquí y allá. La biodiversidad, además, tiene que dejar de ser vista como algo puramente escénico. En la naturaleza, el Ecuador tiene su mejor recurso, enfatizó. Muchas plantas y animales son verdaderas farmacias vivientes, en donde se esconde la cura de muchas de las enfermedades que atacan o atacarán a los humanos. De igual manera, el científico afirmó que el estudio de estas propiedades debería ser una prioridad del Estado y también de las empresas privadas. Además, el conservar la diversidad ya ha probado ser una fuente de recursos importante. Lo que hace especial al Ecuador, afirma, no es el banano, el petróleo, la palma africana, ni nada de eso. El Ecuador es único en el mundo por su biodiversidad, un recurso sin par, irrepetible y con un potencial económico inmenso. Es hora de darse cuenta de esto, finalizó.<br />  </p>','2010-08-16'),('red-ecoturistica-de-intag','Los Editores','Red ecoturistica de Íntag','','<p> RED ECOTURISTICA ÍNTAG</p> <p style=\"text-align: center;\"> <span style=\"font-size:22px;\"><span style=\"color: rgb(0, 100, 0);\"><strong><br /> ALTERNATIVA DE DESARROLLO </strong></span><br /> </span></p> <p style=\"text-align: center;\"> <span style=\"font-size:22px;\"><span style=\"color: rgb(0, 100, 0);\"><strong>EN ARMONÍA CON EL MEDIO AMBIENTE<br /> </strong></span></span></p> <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/reportaje/intag_rei1.gif\" /></p> <p style=\"text-align: justify;\"> El turismo es una de las principales actividades económicas en el mundo. Trae inversión y empleo, tanto directo como indirecto, y contribuye al crecimiento económico y al progreso social de los países en vías de desarrollo. El turismo requiere una cantidad de servicios: hospedaje, alimentación, transporte, diversión. Un sitio ideal para el turismo cuenta con moradores que tengan conocimiento de la historia, la cultura, los atractivos naturales y la gastronomía de la región. El turismo enriquece al ser humano y le brinda la posibilidad de abrir su mente hacia civilizaciones desconocidas, apasionantes y totalmente diferentes de la suya. <br /> A diferencia del petróleo, el turismo constituye un instrumento de desarrollo sostenible que promueve la riqueza y los valores del país. Llevado a cabo de manera responsable, no deteriora sus recursos naturales y es permanente.<br /> Ecuador es un país con una vasta riqueza natural. La diversidad biológica de sus cuatro regiones lo ubica entre los 17 países más mega diversos del planeta, a pesar de su territorio reducido. Cuenta con alrededor de 1.640 clases de pájaros. Las especies de mariposas bordean las 4.500, los reptiles 345, los anfibios 358 y los mamíferos 258. <br /> El turismo en Ecuador ha empezado a desarrollarse recientemente, gracias a los esfuerzos del sector privado, en gran parte. Y en la actualidad, gobiernos locales, comunidades y organizaciones de base de todo el país han emprendido sus propias iniciativas. La gente de Íntag se ha sumado a este esfuerzo. <br /> <br /> <strong>RECURSOS HIDRICOS, FLORA Y FAUNA</strong><br /> El área de Íntag cuenta con aproximadamente 1.462 kilómetros cuadrados. Está ubicada en el piedemonte occidental de la cordillera de los Andes, en los cantones Cotacachi y Otavalo, en la provincia de Imbabura, al norte del Ecuador. Geográficamente, se sitúa entre 0º 30´ y  0º 20´ de latitud norte;  y,  79º 0´ y  78º 20´ de longitud oeste. Seis parroquias inteñas están en Cotacachi &ndash;García Moreno, Apuela, Cuellaje, Peñaherrera, Vacas Galindo y Plaza Gutiérrez&ndash; y una, Selva Alegre, pertenece a Otavalo. Su población es de aproximadamente 15 mil habitantes, distribuidos en 76 comunidades rurales.<br /> En términos bio-geográficos, Íntag forma parte de uno de las regiones de vida más importantes de nuestro planeta: la Provincia Florística, o Bioregión, del Chocó, que se extiende desde el sur de Panamá, todo la costa del Pacífico de Colombia y el noroccidente del Ecuador, en la provincia de Esmeraldas y el occidente de las provincias de Carchi e Imbabura. De hecho, esta bioregión, está catalogada como una de las diez regiones más importantes en el mundo desde el punto de vista biológico (Fuente: www.biodiversityreporting.org).<br /> En la accidentada topografía de la extensa zona de Íntag nacen numerosas vertientes, quebradas, riachuelos y ríos cristalinos que se originan en la ceja de montaña y las estribaciones de la cordillera del Toisán. Este sector es uno de los más ricos en recursos hídricos en todo el país.<br /> <br /> <strong>¿CÓMO NACE LA REI?</strong><br /> En la zona de Íntag, una de las organizaciones que promueven el aprovechamiento sostenible de los recursos naturales es la Red Eco turística de Íntag (REI). Fue creada en el 2006 como el Comité Eco turístico de Íntag (CEI). El objetivo fue definir los grupos de turismo de la Zona. El 1 de abril del 2008, los grupos se legalizaron como una Red a través de una asamblea general que se llevó a cabo con la Federación de Turismo Comunitario del Ecuador (FEPTCE).<br /> <br /> <strong>¿QUÉ BUSCA?</strong><br /> La Red de Turismo  de Íntag busca articular las actividades turísticas de las comunidades de la Zona. Su intención es desarrollar un modelo de turismo comunitario propio que consolide la economía local y fomente fuentes de empleo. La REI cuenta con 11organizaciones de base. Entre ellas están grupos de jóvenes, de mujeres, de productores y de ambientalistas que pertenecen a varias comunidades. <br /> Gracias al apoyo del Gobierno de las Islas Baleares, la ONG Española Fundación Ayuda en Acción  y su socio estratégico PRODECI trabajan para fortalecer la gestión organizativa y de emprendimiento de la REI. Buscan la sostenibilidad de la REI a través del proyecto &ldquo;Turismo sostenible como alternativa de desarrollo en la zona de Ìntag-Ecuador&rdquo;.<br /> Actualmente, la Zona cuenta con un plan de turismo zonal fruto del esfuerzo colectivo de varias instituciones públicas y privadas.<br /> <br /> </p> <p style=\"text-align: justify;\"> <strong>Organizaciónes:</strong></p> <p style=\"text-align: justify;\"> <br /> <img align=\"left\" alt=\"alt\" border=\"1\" hspace=\"8\" src=\"/images/stories/reportaje/intag_mujer-medio-amb.gif\" style=\"width: 126px; height: 97px;\" /><strong>Mujer y Medio Ambiente,  Plaza Gutiérrez - Palo Seco</strong></p> <p style=\"text-align: justify;\"> Confecciona y comercializa artesanías hechas de cabuya, las que son ofrecidas a turistas que visitan sus comunidades, y en el mercado artesanal de Otavalo.</p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: justify;\"> <br /> <img align=\"right\" alt=\"alt\" border=\"1\" height=\"91\" hspace=\"8\" src=\"/images/stories/reportaje/intag_aacri.gif\" style=\"width: 114px; height: 91px;\" width=\"114\" /><strong>Asociación Agro Artesanal de Caficultores Río Íntag (AACRI)</strong><br /> Sede: Apuela<br /> Trabaja en toda la zona de Íntag    Exporta café orgánico hacia  Japón, Francia y España. Son alrededor de 300 socios en todo Íntag. Además llevan turistas a sus fincas cafetaleras demostrativas.</p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: justify;\"> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"95\" hspace=\"8\" src=\"/images/stories/reportaje/intag_cabaas-junin.gif\" style=\"width: 126px; height: 95px;\" width=\"126\" /><strong>Turismo Comunitario </strong><br /> Eco-Junín - Chalguayaco Alto<br /> Chalguayaco Bajo    Proyecto creado por 40 familias como una alternativa de desarrollo, generadora de trabajo y como una estrategia para frenar la minería de explotación de cobre a cielo abierto; se ofrece caminatas por bosques y reservas, estadía y comida en la cabina comunitaria, charlas con comuneros.</p> <p style=\"text-align: justify;\"> <br /> <img align=\"right\" alt=\"alt\" border=\"1\" hspace=\"8\" src=\"/images/stories/reportaje/intag_el-rosal.gif\" style=\"width: 129px; height: 98px;\" /><strong>Grupo de Mujeres El Rosal</strong><br /> El Rosal    Un grupo de mujeres que se especializa en la elaboración de jabones y shampoo en base de sábila y otros ingredientes de los bosques y las fincas inteñas. Sus productos están en hoteles y restaurantes del norte del país y exportados a España, a través de las redes de comercio justo.</p> <p style=\"text-align: justify;\"> <strong><br /> </strong><img align=\"left\" alt=\"alt\" border=\"1\" height=\"100\" hspace=\"8\" src=\"/images/stories/reportaje/intag_natouragua.gif\" style=\"width: 134px; height: 100px;\" width=\"134\" /><strong>Asociación Natouragua</strong><br /> Nangulví   </p> <p style=\"text-align: justify;\"> Ofrece tours pequeños alrededor de Nangulví; cabalgatas, ciclismo y caminatas a fincas agroecológicas, cascadas y miradores cercanos, como complemento a los servicios de aguas termales del Complejo Turístico Nangulví.</p> <p style=\"text-align: justify;\"> <br /> <img align=\"right\" alt=\"alt\" border=\"1\" height=\"109\" hspace=\"8\" src=\"/images/stories/reportaje/intag_turismo-manduriacos.gif\" style=\"width: 146px; height: 109px;\" width=\"146\" /><strong>Comité de Turismo de Manduriacos</strong><br /> Chontal-Magdalena Alto-San José de Magdalena-Reserva Los Cedros   </p> <p style=\"text-align: justify;\"> Conformado por doce familias que cuentan con albergues familiares. Un porcentaje del dinero que ingresa a través de los grupos de turistas se destina a la comunidad, a fin de cubrir necesidades en los campos de salud, educación, etcetra.</p> <p style=\"text-align: justify;\"> <br /> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"90\" hspace=\"8\" src=\"/images/stories/reportaje/intag_cuellaje.gif\" style=\"width: 130px; height: 90px;\" width=\"130\" /><strong>Grupo de Jóvenes Cuellaje Turístico</strong><br /> Cuellaje   </p> <p style=\"text-align: justify;\"> Grupo de 28 jóvenes que trabajan impulsando el desarrollo del turismo de aventura en ríos y cascadas (cayoning y tubing), paseos a caballos y danza andina.</p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: justify;\"> <img align=\"right\" alt=\"alt\" border=\"1\" hspace=\"8\" src=\"/images/stories/reportaje/intag_ciclistico-plaz.gif\" style=\"width: 134px; height: 106px;\" /><strong>Grupo Ciclístico Ecológico de Plaza Gutiérrez </strong><br /> Plaza Gutiérrez-Palo Seco   </p> <p style=\"text-align: justify;\"> Grupo integrado por 19 jóvenes que trabajan en actividades de aventura (trekking, mountain biking) y visitas a fincas ecológicas.</p> <p style=\"text-align: justify;\"> <br />  </p> <p style=\"text-align: justify;\"> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"97\" hspace=\"8\" src=\"/images/stories/reportaje/intag_asociacion-agroecol.gif\" style=\"width: 147px; height: 97px;\" width=\"147\" /><strong>Asociación de Campesinos Agroecológicos de Intag </strong> <br /> Peñaherrera-Barcelona-Cerro Pelado-El Paraíso, El Cristal, Pucará, Cazarpamba   </p> <p style=\"text-align: justify;\"> Organización de agricultores con iniciativas de agroturismo; ofrecen paseos por fincas agroecológicas.</p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: justify;\"> <br /> <img align=\"right\" alt=\"alt\" border=\"1\" height=\"82\" hspace=\"8\" src=\"/images/stories/reportaje/intag_gadi.gif\" style=\"width: 134px; height: 82px;\" width=\"134\" /><strong>Grupo de Mujeres GADI</strong><br /> Pucará   </p> <p style=\"text-align: justify;\"> Grupo de mujeres que confeccionan ropa deportiva, ofrecen alimentación, turismo vivencial y visitas a trapiches (destilación del licor de caña de azúcar) y visitas a talleres de elaboración de  cabuya.</p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: justify;\"> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"105\" hspace=\"8\" src=\"/images/stories/reportaje/intag_gran-valle.gif\" style=\"width: 157px; height: 105px;\" width=\"157\" /><strong>Corporación Talleres del Gran Valle</strong><br /> Magdalena Bajo-Valle de los Manduriacos   </p> <p style=\"text-align: justify;\"> Organización de agricultores con varias iniciativas de industrialización de productos que ponen a disposición de los mercados; los turistas pueden visitar a estas iniciativas pueden ser visitadas, por turistas; se cuenta con un hotel comunitario.<br />  </p> <p style=\"text-align: justify;\"> </p> <p style=\"text-align: justify;\"> <br /> <br /> <strong>NOTAS DE SOCIOS:</strong><br /> <br /> La Asociación de Mujeres El Rosal está vinculada a la Red Eco turística de Íntag desde 2006. Esto le ha permitido beneficiarse de: intercambio cultural, creación de fuentes de trabajo, desarrollo humano, aprovechamiento de lugares atractivos, promoción de productos elaborados en la comunidad de El Rosal. El compromiso es dar un servicio de calidad, y ser un grupo 100 por ciento comprometido a este nuevo modelo de desarrollo sustentable.<br /> Por su parte, Hugo Montenegro, miembro de la REI, piensa que estar unidos es mejor para vender la imagen de Íntag. El señor Montenegro dijo que como socio ha recibido en el 2010 más de 100 turistas. El paquete consiste en dar un paseo por la finca de café, dar una charla donde narra la historia y la apuesta de desarrollo que persigue la Zona. El refrigerio consiste un jugo natural con la fruta de la temporada y finaliza con una cabalgata a la comunidad de Balsapamba. &ldquo;Recibo 13 dólares por la prestación de estos servicios por turista, lo cual me ha ayudado con los gastos de mi familia&rdquo;,  afirma. El señor Montenegro está seguro que la integración de más grupos y organizaciones es fundamental para promocionar la Zona. El turismo tiene que ser bien explotado en armonía con el ambiente, concluyó.<br /> <br /> <strong>LOGROS HASTA EL MOMENTO </strong><br /> La REI está consolidada gracias al apoyo de la Fundación Pro Derechos Ciudadanos  (PRODECI). Cuenta con infraestructura y equipos básicos (botes, bicicletas y el equipo completo para deportes extremos) y ha logrado capacitar prestadores de servicios. Además, se adecuaron las instalaciones del centro de operaciones y tres telecentros  ubicados en Chontal, Cuellaje y Nangulvi. Asimismo, cinco senderos de la Zona están señalizados: Machetes-Plaza Gutiérrez, Pucará-Plaza Gutiérrez, Cuellaje-Lagunas de Piñán, Junín-Cascadas Escondidas (Reserva comunitaria de Junín) y Chontal Bajo-Cascada Salto del Puma. <br /> El objetivo principal es el de dar trabajo a la gente de Íntag. Para fortalecer las capacidades locales se contó con especialistas en los temas de los talleres, entre eston técnicos del Gobierno Provincial,  consultores externos, técnicos de la Cruz Roja, entre otros. Los talleres fueron presenciales a 120 socios y socias que ofertan servicios turísticos-hoteleros. Capacitaron a 14 socios en turismo activo y de aventura en la especialidad de canyoning, barranquismo y aguas blancas (rafting), rescate acuático y primeros auxilios. Sensibilizaron a niños y comuneros en el manejo de los residuos sólidos en veinte talleres Además, dictaron cursos y talleres abiertos a integrantes de la REI y otros actores involucrados en turismo en: manipulación de alimentos, recetas tradicionales y repostería; mantenimiento de piscinas, atención y servicio al cliente, habitaciones y pisos, recepción y cosmetología en la Comunidad de El Rosal. Estos últimos cursos fueron en convenio con la Unidad Ejecutora del Sub-Proyecto &ldquo;Fortalecimiento de la cadena productiva de operaciones turísticas comunitarias&rdquo;. <br /> <br /> <strong>El Presidente de la REI comenta:</strong><br /> Robinson Guachagmira, presidente de la Red Eco-turística Íntag afirma que ésta funciona como una plataforma social que articula a los prestadores de servicios de forma efectiva. La Red ha identificado las áreas de acción y ha generado un plan turístico zonal para aplicarlo y ejecutarlo entre las diferentes instituciones involucradas en turismo comunitario, afirma. Esto, asegura, ha provocado la creación de una Operadora Zonal en Íntag, programas de capacitación en prestación de servicios y atención al cliente, y asegurará nuevas capacitaciones en idiomas y guianza.<br /> La REI está fortaleciendo la organización mediante actividades de marketing y la oferta de servicios y productos más completos. Para lograrlo, están trabajando con el Municipio de Cotacachi, la Asamblea de Unidad Cantonal y ONG, comentó el presidente. El objetivo es  definir los roles, funciones y responsabilidades de cada uno de  los organismos en el ámbito turístico. Con esta meta se han realizado varios talleres para elaborar el plan de turismo y una ruta turística que integre a las tres zonas del Cantón. Para esto fue necesaria una invitación amplia a todos los prestadores de servicios, y las personas que desarrollaron la Ruta Ecoturística &ldquo;Valle Sagrado Intag&rdquo; alrededor de la cual se desplegarán las rutas específicas de interés según los turistas que visiten nuestra zona, afirmó el señor Guachagmira. También mencionó que, a través de la escuela de gestión pública, han trabajado con el Municipio, la AUCC y la REI. <br /> Según la ingeniera Patricia Cabezas,  acercamiento a las organizaciones es importante porque ellas son la esencia de la REI y las que pueden sacar a flote el proyecto. El reto de la organización es construir una operadora turística. Para esto se tiene que contratar una consultora para que recoja todas las ideas de las organizaciones socias, estandarizar precios y todos los componentes que requiere una empresa turística. Todos tienen  que tener claro que el turismo comunitario es de ayuda, no una competencia desleal. También la diversidad de ofertas que se tenga dependerá para que la operadora arme los paquetes o tours. La producción artesanal juega un papel importante en el turismo sin dejar de lado el alojamiento. <br /> Según la ingeniera Cabezas, quedan siete meses para que la operadora turística entre en funcionamiento. Será una empresa de Íntag para Íntag, manejada por los y las inteñas; de esta manera, el turismo será un puntal económico de la zona, aseguró. Y dijo que cada organización que pertenezca a la REI tiene que tener un certificado de normas de calidad.<br /> Para asegurar esto harán intercambio de experiencias con otras operadoras y giras de observación. Y para que exista una mejor comunicación, cada organización tiene que tener un representante en la REI para que esté al tanto de los avances o dificultades y sirva de mediador o multiplicador de la información.     <br />  </p> <p style=\"text-align: justify;\"> </p> <p style=\"text-align: justify;\"> <span style=\"font-size:18px;\"><strong>Servicios y paquetes turísticos</strong></span><br /> La REI ha recibido a grupos, turistas independientes y estudiantes. Por ahora hay varios tours planificados: de aventura, con nivel 3,5  sobre 5 de dificultad , y de salud que es apto para todo público. Los dos tours se promocionan a través de la página web: www.intagturismo.com Además la REI ofrece alquiler de equipos y varios servicios turísticos:<br /> <br /> <br /> <strong>ALQUILER DE EQUIPOS</strong><br /> &bull;         Carpas de montaña con capacidad de 6 personas<br /> &bull;         Sleepings<br /> &bull;         Bicicletas de montaña<br /> &bull;         Cuadrones</p> <p style=\"text-align: justify;\"> <strong>SERVICIOS</strong><br /> En turismo de aventura: <br /> Rafting<br /> Cayoning<br /> Rappeling<br /> Cabalgatas<br /> Trekking<br /> Mountain biking<br /> Pesca deportiva</p> <p style=\"text-align: justify;\"> <strong>En agroturismo:<br /> </strong></p> <p style=\"text-align: justify;\"> Visita a fincas integrales de café orgánico, caña de azúcar; a parcelas agroforestales donde se dan a conocer los diferentes tipos de conservación de suelos, la recuperación de semillas y la diversidad de cultivos.<br /> Observaciòn de la desfibracion de cabuya, y de la produccion de gas natural (biodigestores).<br /> Turismo comunitario familiar (convivencia).<br /> En turismo artesanal:<br /> Visita a las artesanas en cabuya, lufa, tagua, confección y bordados, productos cosmetológicos a base de aloe vera y de papel reciclado.</p> <p style=\"text-align: justify;\"> <strong>En turismo ecológico:</strong><br /> Observación a ríos, cascadas, lagos y paisajes.<br /> Visita a bosques nublados como La Florida, Cerro Quinde, Los Cedros y la Reserva Hídrica del Paraíso.<br /> Paseo por las comunidades con explicaciones e historia de la misma.<br /> Observación de gran variedad de mariposas y flora en general.<br /> Visita a miradores.<br /> <br /> <span style=\"color:#006400;\"><strong>TOURS DE FIN DE SEMANA</strong></span><br />  </p> <p style=\"text-align: justify;\"> <strong>Ruta: Íntag es aventura</strong><br /> Se realizan las siguientes actividades: recorrido en chiva, mountain biking, trekking, visita a las ruinas de Gualimán, canopy, disfrute de las aguas termales, visita a fincas orgánicas y molienda de caña, observación de flora y fauna, noche cultural.<br /> El costo por persona por fin de semana es de: $60 (incluye estadía, alimentación y transporte Cotacachi - Intag)</p> <p style=\"text-align: justify;\"> <strong>Ruta: Íntag es salud y vida</strong><br /> Se realizan las siguientes actividades: recorrido en bus expreso observando la flora y fauna, paseo por la comunidad y relato de su historia, degustación de platos típicos, disfrute de las aguas termales, visita a fincas agroecológicas, ganaderos y a la molienda de caña, noche cultural.<br /> El costo por persona por fin de semana es de: $40 (incluye estadía, alimentación y transporte Cotacachi - Intag.)<br /> <br /> </p>','2011-06-07'),('reflexiones','Hugo Robalino Fernández','REFLEXIONES DE UN CIUDADANO DE A PIE ',' <p style=\"text-align: justify;\">¿Se han dado cuenta del repunte de popularidad del ñaño del presidente Correa? Deduzco que al menos una entrevista por día debe haber “ofrecido” a los medios de comunicación en la primera semana de diciembre y por cierto ahora le dan gran apertura en los canales de televisión “independientes”. <br />Hace poco comentaba el ñaño Fabricio sobre la Ley de Comunicación, denominado por otros “ley mordaza”. Decía que es un proyecto de ley tremendamente malévolo y ponía en alerta a todos los ciudadanos ya que este proyecto de ley podría pasar sin chistar en la Asamblea porque, a pesar que existen algunas voces discrepantes al interior del movimiento gobiernista, cuando llega Patiño, decía, a los “talleres” de Alianza País, que solamente le basta con repartir coscachos a los sumisos asambleístas de Movimiento País y estos, como mansos “corderos”, alzan la mano. <br />Espero que esto no sea verdad, no porque le tengo terror a los coscachos –al fin de cuentas no soy asambleísta, además mi mamá me jalaba de la oreja cuando hacía alguna travesura– sino más bien porque espero que los inteños que ganaron las elecciones con el auspicio del partido de Gobierno sean valientes –como lo son toda la gente de este lindo valle– y escuchen la voz de su gente, que pide que trabajen sin resentimientos y con el mismo perfil ambientalista que ha caracterizado a esta noble zona a través de más 12 años.</p> <p style=\"text-align: justify;\"> </p> ',' <p>Así lo confirma la resolución de la última Asamblea de Unidad Cantonal de Cotacachi, realizada en la parroquia de Imantag, donde hubo una gran concurrencia de inteños que ratificaron su compromiso por defender los recursos naturales.<br />Así que confío en los dirigentes valientes y libres que sabrán hacer lo mejor por sus parroquias. Porque de lo contrario, penoso sería enterarnos de algún coscacho enviado desde el supremo de la provincia, de Carondelet o de un tal Patiño, y jocoso sería enterarnos a través del colega columnista Ajírocoto.<br />“¡Toma Coscacho!” Pintoresco, ¿verdad? Algunos amigos comentan que oyendo u observando al ñaño mayor se sacan el estrés y oyendo al menor que está en Carondelet les resulta el efecto contrario. Juzgue usted, amigo lector.</p> <p> </p>','2010-03-12'),('represa-en-chone-afecta-a-familias','Boris Zambrano','Represa en Chone afecta a familias','',' <p style=\"text-align: justify;\"> Nota de la editora: El escrito que reproducimos a continuación es una especie de carta abierta dirigida por el señor Boris Zambrano, morador de Río Grande, en donde el Gobierno quiere construir una represa desalojará a 1600 familias agricultores. <br /> <br /> Usted y yo somos scouts y los scouts debemos aborrecer la mentira, ha dicho durante el último enlace ciudadano. Pero me apena sobre manera que a usted le hagan mentir porque le informan mal, y, como pidió que alguien le indique, yo le indico:<br /> No son &ldquo;diez o quince familias prepotentes&rdquo; las que se oponen a la construcción de una represa en Río Grande. En este lugar fértil de Chone viven más de 1600 familias a las que las distintas consultoras y funcionarios de su gobierno NUNCA les consultaron sobre dicha obra como manda el artículo 398 de la Constitución y el 28 de la Ley de Gestión Ambiental. Río Grande tiene, desde el 13 de marzo de 1997, un Comité Central de Comunidades Campesinas que JAMÁS fue contactado e<br /> identificado en los estudios. Simplemente dieron por hecho que a los montubios nos pueden aplastar como en el pasado porque sus funcionarios actúan con las mismas estrategias de la vieja partidocracia que usted tanto critica. Por lo tanto, como nunca se hizo la socialización como está en la ley, el contrato firmado en julio pasado es nulo. Entérese también que la SENAGUA [Secretaría Nacional de Agua] declaró de utilidad pública el predio de Alfredo Zambrano Vera SIN NINGUNA NOTIFICACIÓN O AVISO PREVIO.<br /> Yo le indico que acá no hay politiqueros. Nunca nos hemos reunido con Lucio, Gilmar, Pachacutik, MPD o el Prian. Nuestra lucha no es política sino legal, en defensa de nuestras tierras en la que producimos para sustentar a nuestras familias y al país ya que generamos cerca de 20 millones de dólares anuales vitales para la economía local. Estamos ejerciendo el derecho a la resistencia como lo manda el artículo 98 de la Constitución, sin caer en las provocaciones que somos objeto cada día.<br /> Dice usted que &ldquo;los choneños tenemos que dejar el julepe y reclamar por estas obras&rdquo; que son parte del PPM Chone. Eso es lo que estamos haciendo porque hemos denunciado decenas de irregularidades que constan en los estudios a los que tuvimos acceso recién en marzo de este año, producto de un juicio de acceso a la información que seguimos a SENAGUA porque siempre nos negó la información pertinente. Le indico que cerca del 85 por ciento de los chonenses no queremos una represa en suelo de alta sismicidad porque agua hay suficiente en el valle y estamos muy cerca del Proyecto Carrizal Chone.<br /> Que se llevarán los sesenta millones a otro lado, ha manifestado, pero no le parece que mejor los invierta en un nuevo sistema de alcantarillado pluvial de la ciudad de Chone que se inundó hace unos días con una leve lluvia; contrate además la nueva planta de agua potable porque la que tenemos ya no abastece y es obsoleta; invierta en encauzamientos, desazolve de ríos y en completar el sistema de purificación de aguas servidas que hoy caen al antiguo encauzamiento y que serán<br /> derivadas al humedal La Segua, sitio que no debe ser tocado ni directa ni indirectamente con las obras del PPM Chone, tal como consta en un estudio.<br /> No queremos que nadie muera, Presidente. En Río Grande no hay delincuentes. Lo que queremos es vivir en paz con nuestras familias. Paz que hoy no tenemos porque cada día nos amenazan hasta con militarizarnos ¿Qué delito hemos cometido? ¿Nos criminalizan por defender a la Pachamama? <br /> Finalmente, me permito invitarlo una vez más a nuestra bella tierra para que escuche de viva voz las razones de nuestra resistencia y lucha que llevamos pacíficamente.<br /> No voy a callar, Presidente Correa, porque creo en la democracia al igual que usted. No callaré aunque hace unos meses me llamaron para advertirme que &ldquo;sería muy saludable que me calle&rdquo; y hace un par de días amenazaron a mi esposa con darme una golpiza. <br /> &ldquo;Lo único que necesita el mal para triunfar es que los hombres buenos no hagan nada&rdquo;, dijo Edmundo Burke. Yo creo que usted es bueno. Es hora de que actúe en beneficio de los montubios de Río Grande.     <em>                                    <br /> Correo electrónico del autor: boriszambrano@yahoo.es<br /> </em></p> <p style=\"text-align: justify;\"> <em>Fuente: http://www.agenciaecologista.info/editoriales/230-yo-le-indico-presidente-correa<br /> <br /> <br /> </em></p>','2011-10-11'),('rio-intag-contaminado','Pablo Vetancourt','Río Íntag contaminado','',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notizonacantprov/img_3824.gif\" /></p> <p style=\"text-align: justify;\"> Las aguas servidas no bien tratadas provocan altos niveles de contaminación. Pero muchas veces nadie conoce la intensidad hasta que los análisis lo demuestren. Éste es el caso de las aguas servidas del Complejo Ecoturístico Nangulví que desembocan al rio Íntag. La doctora Karen Knee, científica estadounidense, en su análisis de las aguas encontró &ldquo;altas concentraciones de metales, nutrientes y bacterias, y un nivel bajo de oxígeno disuelto&rdquo;. Llevó a cabo la investigación entre los meses de agosto del 2010 y febrero del 2011.<br /> El Periódico <em><strong>ÍNTAG </strong></em>conversó con el administrador y el presidente del Comité encargado del manejo del Complejo Ecoturístico Nangulví el 26 y el 31 de agosto, respectivamente. Ellos afirmaron que el sistema de tratamiento fue construido hace varios años y que podría ser que no esté funcionando bien. <br /> A continuación les ofrecemos detalles de la investigación y la reacción de los encargados del Complejo. <br /> <br /> <strong>Metales en niveles normales</strong><br /> Las muestras tomadas por la científica son de un tubo de cemento y un pequeño pantano en donde se descargan las aguas del Complejo. Pero la doctora Knee también analizó las aguas del rio Íntag aguas arriba y abajo del punto en que se desembocan las aguas servidas del Complejo en el río. <br /> En los análisis, la doctora encontró altos niveles de manganeso y arsénico. En el pantano, 338 partes por billón (ppb) de arsénico y en el tubo 1316 ppb. Estos metales se encuentran normalmente en las aguas térmicas en todo el mundo. La doctora explica que el nivel elevado, sobre todo del arsénico, puede ser peligroso para beber pero no para bañarse. Dice, además, que no ha hecho un análisis del agua dentro de las piscinas para saber si la concentración de los metales es la misma. <br /> <br /> <strong>Otros elementos no tan normales</strong><br /> En cuanto a las concentraciones de nutrientes, se encuentra un alto nivel de fosfato y amonio. De fosfato, las muestras tomadas del pantano contenían 610 miligramos por litro (mg/L) y, en el tubo, 543 mg/L. En el pantano, había 2331 mg/L de amonio mientras la muestra tomada del tubo arrojó un resultado de 243 mg/L. El fosfato puede venir de aguas térmicas, jabones y detergentes o aguas negras. En cuanto al amonio, lo más probable es que venga de aguas negras. <br /> Aunque las muestras presentan altos niveles de contaminación, sobre todo las del pantano, la doctora Knee cree que no existe riesgo para los seres humanos o animales que usan el agua del río. <br /> Sin embargo, la presencia de bacterias sí, es motivo de preocupación, según los resultado del análisis. En el pantano se encuentran 8000 coliformes y 3000 E. Coli en cada 100 mililitros (ml) de agua. Los niveles en el tubo presentaron índices más bajos: 1500 coliformes y 40 E. coli en 100 ml de agua. La doctora afirma que el agua que sale del tubo tiene niveles normales pero que la del pantano es demasiado elevada. Estas bacterias por lo general están en las heces de seres humanos, animales y aves. La doctora cree que la presencia de bacterias es porque hay una mezcla de aguas negras en el pantano.  <br /> El oxígeno disuelto muestra 1.8 partes por millón (ppm) en el agua del pantano y 5 ppm en las del tubo. Este nivel de oxígeno no es suficiente para sostener la vida acuática de un río. Puede haber dos causas del bajo nivel de oxígeno: aguas térmicas del interior de la tierra desde donde no hay aire o porque los microorganismos que se encuentran en las aguas negras están consumiendo el oxígeno. Sin embargo, la científica explica que esos niveles contribuyen muy poco al caudal de rio Íntag y que no provoca tantos riesgos para la vida acuática. <br /> Las cifras encontradas en el río son completamente reales, según la doctora Knee. Pero explica que los niveles pueden variar de un día para el otro, dependiendo de las concentraciones de heces. Puede ser que, antes de tomar la muestra, un animal ensucie el agua y eleve la contaminación. La doctora afirma que no pudo tomar muestras de cada lugar y que no tiene certeza de que &ldquo;los sitios limpios siempre están limpios ni que los [&hellip;] sucios siempre están sucios&rdquo;.<br /> La doctora informa que con los contaminantes de las aguas térmicas, como los metales, el fosfato y el amonio, no se puede ni se debe hacer nada puesto que son naturales. Mientras en el caso de las bacterias, es probable que las aguas negras se estén mezclando, sobre todo con las del pantano y luego contaminado el rio Íntag. Señala que la probabilidad de la contaminación se deba a la mezcla de las aguas termales con las negras. Sin embargo, no hay certeza puesto que no hay un análisis de la mezcla de las dos. La científica recomienda que se haga una inspección para averiguar y eliminar la fuente de contaminación.<br /> La doctora afirma que los riesgos para la gente y para la vida acuática del rio Íntag no son muy graves. Sin embargo, si alguien tiene contacto con el agua que sale del pantano, es probable que se enferme con diarrea o infecciones a la piel.<br /> <br /> <strong>Reacciones de los encargados</strong><br /> Gustavo León, presidente del Comité creado por todos los presidentes de las comunidades de Peñaherrera para el manejo del Complejo, dijo que no solo de este sitio se contaminan las aguas del Íntag. La contaminación viene desde los depósitos de los alcantarillados de las cabeceras parroquiales de Apuela y Cuellaje y de otras casas y sitios turísticos que están en el valle cerca al río Íntag. <br /> Por su parte, Antonio Obando, administrador del Complejo, dijo que existe un sistema de tratamiento de las aguas, tanto del restaurant como de las cabañas y piscinas, antes de que se desemboquen al rio. Explicó que hay un sistema para el reciclaje de las grasas saturadas, especialmente del restaurant. También hay un sistema de reciclaje para los desechos sólidos.<br /> Afirmó que sí es necesario analizar el sistema de tratamiento para saber cuándo fue construido y en qué condiciones se encuentra. Aunque aseveró que por el momento está funcionando bien porque no hay olores, dijo que sí es pertinente darle mantenimiento y remodelarlo. Por otro lado, el señor León afirmó que cuando la parroquia recibió el Complejo, el sistema ya estaba saturado. Dijo que para arreglar eso se requiere de un presupuesto alto y que el Comité le pidió ayuda al Municipio de Cotacachi porque es de su competencia, pero no hubo respuesta.  <br /> En respuesta a estas explicaciones por parte de los encargados, la doctora Knee indicó que tomó muestras del rio Íntag antes de las descargas de las aguas del Complejo y fueron mucho más limpias. También analizó las aguas de rio abajo de las descargas del centro poblado de Apuela y Cuellaje. Dijo que hay más contaminación en las muestras de Apuela que en las de Cuellaje. Aclaró que la contaminación del agua de Apuela se disuelve rápidamente al mezclarse con el caudal del rio y no es tan peligrosa como las muestras tomadas del agua proveniente del pantano, cerca del Complejo Nangulví. Afirma que los niveles de contaminación en las fuentes cerca del Complejo no provienen ni de Apuela ni de Cuellaje. Sin embargo, afirma que los desechos de los centros poblados, especialmente de Apuela, sí presentan problemas para las aguas de los ríos.<br /> <strong><br /> Soluciones</strong><br /> El administrador afirmó que el Complejo se encuentra en una etapa de remodelación y que el sistema de tratamiento de aguas también está dentro de los planes. Dijo que harán un análisis de las aguas termales y a la vez de las servidas. Indicó, además, que va a considerar hacer un estudio de todas las áreas para identificar problemas y darles solución lo más pronto posible. En cuanto al problema de contaminación, aseguró que hará un análisis a mediano plazo y buscar alguna alternativa para solucionar el problema. <br /> Por el momento, la aguas de los diferentes sitios son dispersadas de manera individual pero en lo posterior van a implementar un buen sistema de tratamiento para que haya menos contaminación, informó el administrador. Sin embargo, dijo que dentro del Complejo ya están trabajando con el objetivo de contaminar menos. Los desechos de la cocina son reciclados para la creación de abono orgánico. Por su parte, el presidente del Comité aseguró que está comprometido en hacer los arreglos necesarios para bajar los impactos ocasionados en el Complejo. <br /> El señor Obando, por su parte, hizo votos para que cuando haya este tipo de estudios y se encuentren anomalías, los investigadores también sugieran posibilidades para mejorar o evitar esos problemas. &ldquo;Al problema hay que darle una solución&rdquo;, dijo.<br /> El administrador también nos contó sobre los trabajos y las proyecciones que tiene para el Complejo Nangulví. Trabaja en un proyecto con la ayuda de de la Universidad Técnica del Norte para conseguir más flujo de aguas termales. La idea es recuperar las que se están drenando y recoger las pequeñas vertientes que no entran al sistema de recolección de agua. El objetivo es implementar más piscinas. Asimismo, están trabajando en la construcción de más áreas deportivas, áreas verdes y un parqueadero. &ldquo;Hay que reinvertir lo poco que queda&rdquo;, finalizó.</p>','2011-10-11'),('se-criminaliza-la-protesta','Los Editores','Se criminaliza la protesta','','<p> MIENTRAS LOS DE CUELLO BLANCO ANDAN IMPUNES&hellip;</p> <p style=\"text-align: center;\"> <span style=\"color:#006400;\"><span style=\"font-size: 24px;\"><strong>SE CRIMINALIZA LA PROTESTA<br /> </strong></span></span></p> <p style=\"text-align: justify;\"> Vivimos en un mundo al revés. Basta un vistazo al número anterior del Periódico ÍNTAG: violaciones de derechos, minería ilegal, concesiones dudosas, desperdicio de fondos, obras a medias y con sobreprecio, decretos de excepción emitidos a diestra y siniestra que ponen en entredicho la democracia en el país. ¿Quién está siendo investigado o está en la cárcel por todas estas irregularidades? A todas luces, nadie. Porque estos actos son &ldquo;crímenes de cuellos blanco&rdquo; y puesto que sus autores, cómplices y encubridores gozan de poder político y económico, son impunes. A menos cuando meter al implicado en cana resulte en réditos políticos para un opositor. <br /> En plena Revolución del Siglo XXI, con la primera Constitución que defiende la naturaleza y el derecho del buen vivir, lo que sí está penado en Ecuador es protestar. De hecho, y a pesar de constar como otro derecho en dicha Constitución, disentir se ha convertido en un delito tan grave que le puede costar a un pobre individuo hasta ocho años de cárcel y multas millonarias. Un total de 268 personas están siendo enjuiciadas por terrorismo, sabotaje u obstrucción de carreteras, según la hermana Elsie Monje, presidenta de la Comisión Ecuménica de Derechos Humanos (CEDHU). <br /> Hasta el cansancio hemos reportado sobre casos de minería ilegal en Íntag. La empresa CECAL, por ejemplo, ha operado en la Zona durante décadas sin ni siquiera contar con permisos oficiales y un Estudio de Impacto Ambiental (EIA). Sin embargo, cuando la gente en los alrededores de sus instalaciones protesta por los impactos de la actividad en su salud, su vida diaria, sus cosechas, llegan batallones de policías para proteger a la sagrada propiedad privada mientras campesinos son demandados. Sólo muy, pero muy de vez en cuando, una autoridad hará cumplir la ley, como en el caso del proyecto minero a cargo de CODELCO, denominado &ldquo;Los Mandariyacos&rdquo; (los consultores ni se han molestado en averiguar cómo se escribe el nombre de la Zona), donde el Ministerio del Ambiente ha rechazado el estudio de impacto ambiental por padecer de una serie de omisiones y errores. Sin duda, éste es un triunfo de la población inteña, por lo menos por el momento (ver página &hellip;).<br /> Pero en la mayoría de casos, el Gobierno aplica el Código Penal para reprimir la resistencia. Sin embargo, la protesta no es más que una respuesta ante la indiferencia, la ignorancia y las frustraciones producidas por autoridades y funcionarios de un Gobierno que viola su propia Constitución vanguardista en temas de derechos colectivos y de la naturaleza. <br /> Para colmo, este mismo Gobierno ha decretado más de 250 estados de excepción. Esta maniobra le da al Presidente, según la Constitución, hasta 90 días durante los cuales puede limitar o suspender los derechos ciudadanos. Y puesto que se trata de un Gobierno que ha demostrado en múltiples ocasiones que las leyes y la Carta Magna son para los de poncho, los estados de emergencia se prolongan a su antojo. Los perjuicios han sido grandes, según los analistas. Las denuncias de actos de corrupción obrados durante los estados de emergencia son muchas: entrega de recursos a dedo, compras innecesarias, procedimientos dudosos. Durante la emergencia en seguridad, por ejemplo, gastaron más de 400 millones, durante la de salud, mucho más. Ahora están destinados más de 450 millones para la emergencia del Sistema Judicial. Esta última emergencia ya les parece a muchos analistas el colmo de los colmos. ¿Qué pasa con la autonomía de las funciones del  Estado? Cómo puede la administración de justicia gozar de &ldquo;independencia interna y externa &hellip; autonomía administrativa, económica y financiera&rdquo;, si ya está siendo manejada abiertamente por el Ejecutivo? El artículo 168 de la Constitución quedará en papel. Mientras tanto, el Presidente se escuda tras el pueblo: el triunfo del &ldquo;sí&rdquo; en la Consulta Popular le da la facultad de arrogarse aún más poder. <br /> Cuando el Ejecutivo se siente cuestionado, tacha a los cuestionadores de infantiles, corruptos, delincuentes, sicarios de tinta, lacras, mafiosos, etcétera, etcétera, etcétera. Y los enjuicia en las cortes cuyo control ya está ejerciendo. <br /> En cambio, frente a las denuncias de corrupción, el Presidente, lejos de ordenar que se investiguen, responde con agresividad y se justifica aduciendo persecución política. Y ¿quién responde por la evidente persecución política de los cientos de dirigentes y periodistas enjuiciados y amedrentados? <br /> Esperamos que, al igual que con el caso de Codelco en Íntag, sea la ciudadanía organizada que abra los ojos a los asesores, consultores y burócratas para que se haga una verdadera justicia con mayor frecuencia.</p>','2011-10-11'),('se-estudia-los-rios-de-intag','Marjorie Chopin','Se estudia los ríos de Íntag','',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notizonacantprov/intag_hidrointag4.gif\" /></p> <p style=\"text-align: justify;\"> En Intag existen escasos datos de calidad o caudales de los ríos. La mayoría ha sido producida por investigaciones por parte de empresas ajenas con el fin de aprovechar los recursos (proyectos de hidroelectricidad o de minería) o por extranjeros en la elaboración de investigaciones para sus propios fines, generalmente académicos. Como consecuencia, la escasa información disponible está en todas partes y en ninguna. La misión francesa, que está por el año 2011, tiene como uno de sus objetivos establecer un plan de seguimiento de las cuencas, es decir, seguir la evolución de la cantidad y la calidad de los ríos y del clima. A continuación, ofrecemos a los lectores información general sobre las cuencas, la importancia de su seguimiento y el planteamiento de la HidroÍntag en cuanto a este tema.<br /> <strong><br /> ¿Qué significa la frase &lsquo;cuenca hídrica’?</strong><br /> Una cuenca se define como el área de drenaje de un río. Por ejemplo, la cuenca del Toabunchi &ndash;que, debido a su extensión reducida, se define como una &lsquo;micro cuenca’&ndash; forma parte de la cuenca más grande del río Íntag que, por su parte, está incluida en la cuenca del río Guayllabamba que desemboca en el océano. Cada río tiene cuenca propia que puede extenderse desde las mil hectáreas de las más pequeñas, como el río Gualoto o el río Pitura, hasta las 15 000 y más hectáreas de las grandes, por ejemplo, el Magdalena con 15 000 hectáreas, o el Cristopamba con 19 600. <br />  Las cuencas de los afluentes de los ríos Íntag y Guayllabamba; se puede apreciar sus tamaños respetivos.<br /> <br /> Cada cuenca tiene características propias, como pendiente, altura, cobertura vegetal y uso de los suelos (cultivos, potreros, bosques, etcétera). Estas características determinen el funcionamiento hídrico de la cuenca, es decir, cómo se traslada y transforma el agua de la lluvia para formar los ríos. <br /> Así, aparte de los cambios climáticos, cada cambio de uso de los suelos (tierra de cultivos transformada en pastos, bosques en cultivos, pastos en pueblo&hellip;) o actividad tendrá efectos en la calidad y/o la cantidad del agua disponible. Por ejemplo, si se deforesta, el agua de la lluvia no tiene más árboles para retenerla y tiende a fluir más rápidamente hacia los ríos. Esto agrava las crecidas y la erosión de los suelos.<br />  <br /> Representación teórica de cómo cambia el caudal en un río como consecuencia de la lluvia: en la cuenca deforestada, el nivel de los ríos crece más rápidamente, y la crecida es más alta y dura menos; en cambio, en la cuenca sin deforestación, la crecida no es tan pronunciada, viene después y dura más.<br /> <br /> Otro ejemplo, si se usan químicos para los cultivos, llegan al agua o a los sedimentos de los ríos de la cuenca.<br /> <strong><br /> En qué consiste el seguimiento</strong><br /> El monitoreo de las cuencas implica dos cosas: por un lado, registrar los datos climáticos con una estación meteorológica permanente (el viento, la lluvia, la temperatura y así por el estilo) y, por otro, conocer los ríos.<br /> Monitorear los ríos nos permite conocer a lo largo del año la variación y la evolución de:</p> <ul> <li> La candidad de agua: caudales, niveles.</li> <li> La calidad: esto se puede medir in situ, es decir, durante la colecta de muestras. El análisis de dichas muestras permite conocer algunos indicadores básicos, como el pH, la temperatura, el oxigeno disuelto, la conductividad, la turbidez. Para más información, se analizan muestras en un laboratorio para detectar metales y minerales, pesticidas, bacterias específicas y otros elementos requeridos. Por ejemplo, normalmente, se busca las bacterias llamadas coliformes fecales, que son un índice de posible contaminación por materias fecales y, consecuentemente, un riesgo a la salud de las personas que consuman el agua.</li> <li> Los flujos sólidos, es decir, todos los materiales cargados por los ríos. Si son pequeños, están mezclados con el agua y fluyen con la corriente, dándole al agua un aspecto más o menos lodoso. En cambio, los materiales más gruesos, como arena o piedra, se mueven al fondo de los cauces cuando hay más corriente, sobre todo, durante las crecidas.</li> <li>  </li> </ul> <p style=\"text-align: justify;\"> El observador de la comunidad de Cielo Verde viene dos veces por día a leer la regla y anotar el nivel del Manduriaco Grande. Esto permite conocer las variaciones de los niveles de agua, datos esenciales para determinar el caudal del río.<br /> <br /> <strong>Para qué sirve el seguimiento de las cuencas</strong><br /> Los datos de seguimiento pueden servir para:</p> <ul> <li> comprobar que en las tomas, el agua es apta para el consumo humano y, en caso de no serlo, darle el tratamiento adecuado según los elementos indeseados encontrados;</li> <li> controlar la calidad del ambiente acuático y saber si es apto para la vida acuática (peces y otros organismos); un río con pH bajo (5), como el caso del llamado Riachuelo, afluente del Junín, compromete la vida acuática;</li> <li> identificar una posible contaminación al ver uno o varios índices de calidad situados en rangos anormales o evolucionando de manera distinta;</li> <li> entender el funcionamiento de las cuencas y la relación entre la lluvia y los caudales en los ríos;</li> <li> prever o medir el impacto (positivo o negativo) de proyectos (creación de reservas hídricas o bosques protectores, construcción de carreteras, etcétera) o actividades (vertientes de aguas residuales, uso de químicos en los cultivos);</li> <li> adaptar el diseño de nuevos proyectos o actividades y anticipar los impactos que tendrán en la cuenca;</li> <li> y más&hellip;</li> </ul> <p style=\"text-align: justify;\"> <br /> Así, mantener un monitoreo, que implica la construcción y permanente actualización de una base de datos común, permite no perder información y ponerla a la disposición de todo el mundo: agricultores, organizaciones, autoridades, investigadores, moradores en general. Por medio de esto, esperamos lograr una visión global de los recursos hídricos en la zona, identificando posibles problemas o debilidades (sequía, mala calidad) y soluciones.<br /> Por lo tanto, el monitoreo de la zona significa tener una herramienta imprescindible de control y vigilancia para el desarrollo de Íntag. <br /> <strong><br /> Dónde estamos y cuáles son las perspectivas</strong><br /> Desde 2009, los observadores de la red técnica de HidroÍntag están monitoreando los niveles de cinco ríos de la Zona con el fin de transformar los datos de niveles en datos de caudales. Los ríos son:Nalgulví, Toabunche, Manduriaco Grande, Chalguayaco y Pamplona.<br />  <br /> La evolución del río Pamplona aguas arriba de la confluencia con el Íntag, desde 2009. Cada línea corresponde al nivel de agua en la regla durante un año entero, empezando en septiembre. Así podemos conocer las variaciones de las crecientes y estiajes de un año al otro. Se ve, por ejemplo, que el nivel mínimo alcanzado en el transcurso de tres años fue en octubre de 2010.<br /> <br /> A partir de julio de este año, se medirán los caudales de estos cinco ríos con frecuencia a lo largo del año. El 30 de junio y el 1 de julio, se reunirán las personas involucradas en el seguimiento de los niveles y caudales para presentarles los primeros resultados y establecer métodos de trabajo común.<br /> El seguimiento de la calidad del agua todavía está pendiente. Según la demanda y el costo de la inversión, se dará prioridad a ciertos parámetros. No obstante, en vista del interés que existe entre los inteños, HidroÍntag considera el monitoreo de la calidad una prioridad y lo iniciará lo más pronto posible.<br /> Una base de datos geográficos común se establecerá en las próximas semanas. Incluirá los datos disponibles (recorrido de los ríos, carreteras, red de electricidad, sistema de abastecimiento de agua potable, reservas hídricas, límites de cuencas, resultados del seguimiento, etcétera). Servirá como fuente de información para todas las personas y entidades gubernamentales y no gubernamentales interesadas. Para poder usar la base de datos y para preparar mapas, se requieren conocimientos del Sistema de Información Geográfico (SIG). HidroÍntag ofrecerá capacitación en el tema a partir del 16 de agosto, en Peñaherrera. De acuerdo al cupo, están invitados las juntas parroquiales, organizaciones y personas quienes, por razones de trabajo o investigación, tienen que elaborar mapas de cualquier tipo: de límites de propiedad, ubicación de socios, zonas turísticas, etcétera.</p> <p style=\"text-align: justify;\"> <img alt=\"alt\" src=\"/images/stories/notizonacantprov/intag_hidrointag1.gif\" style=\"width: 449px; height: 316px;\" /><br />  </p>','2011-07-21'),('se-pavimentara-la-via-a-intag','Los Editores','Se pavimentará la vía a Íntag','','<p> ESTUDIOS ESTÁN LISTOS</p> <p style=\"text-align: center;\"> <span style=\"color:#006400;\"><span style=\"font-size: 24px;\"><strong>SE PAVIMENTARÁN VÍA </strong></span></span></p> <p style=\"text-align: center;\"> <span style=\"color: rgb(0, 100, 0);\"><span style=\"font-size: 24px;\"><strong>CUICOCHA-APUELA-AGUGRÚN<br /> </strong></span></span></p> <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notizonacantprov/intag_gpi.gif\" /></p> <p> El Gobierno Provincial de Imbabura invirtió 328.000 dólares en la elaboración de los estudios técnicos para la pavimentación del los 61,5 kilómetros de la vía Cuicocha-Apuela-Aguagrún. Según el boletín de prensa difundida por el GPI el 8 de julio, los estudios son &ldquo;el punto de partida para gestionar recursos del Estado y concretar los trabajos en un tramo que enlaza a una extensa zona productiva y conecta directamente con Esmeraldas&rdquo;.<br /> El prefecto Diego García Pozo entregó los estudios al alcalde de Cotacachi, Alberto Anrango, durante la celebración de los 150 años de la creación del cantón. En su discurso, el prefecto García afirmó que la vía &ldquo;traerá progreso y desarrollo para una zona eminentemente productiva&rdquo; y que conectará a Imbabura con la provincia de Esmeraldas. La carretera &ldquo;establecerá nexos comerciales de gran nivel, aprovechando la capacidad productiva diversa que existe en los distintos poblados repartidos, especialmente en la geografía de Íntag&rdquo;.<br /> El boletín hace referencia a tres &ldquo;modalidades&rdquo; estudiadas en cuanto a la pavimentación, pero no ofrece detalles al respecto, aparte del monto que costaría cada una: 13’906.173, 16’771.527 y 18’186.899 dólares. Indica, además, que el prefecto García &ldquo;liderará la gestión ante los máximos niveles gubernamentales para que se haga efectivo el apoyo y así se puede desarrollar los trabajos&rdquo;.<br /> El alcalde Anrango, por su parte, destacó la importancia de los estudios, según el boletín y dijo que ahora &ldquo;esta localidad tiene la posibilidad cierta de aspirar a que se concrete el proyecto por el cual las comunidades de la zona de Íntag han debido luchar desde épocas pasadas&rdquo;.<br /> Finalmente, Gisela Morales, presidente de la Junta Parroquial de García Moreno, dijo que &ldquo;la pavimentación de la carretera es una acción que mejorará las condiciones de vida de miles de pobladores&rdquo;.<br /> En el próximo número del Periódico, esperamos ofrecerles a nuestros lectores detalles de las conclusiones de los estudios citados.<em>MEF</em><br /> <br /> <br /> <em>Pie: El prefecto Diego García Pozo entrega al alcalde Alberto Anrango los estudios del proyecto vial. </em><br /> <br />  </p>','2011-07-21'),('senderos-al-sumak-kawsay','Linda D’Amico','Senderos al Sumak Kawsay','',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" height=\"235\" src=\"/images/stories/notipaismundo/intag_itt4.gif\" style=\"width: 314px; height: 235px;\" width=\"314\" /></p> <p style=\"text-align: justify;\"> Con el apoyo de la ONG alemana GEO, la comunidad de El Paraíso ha logrado comprar su cuenca hídrica y reforestar con más de sesenta mil árboles. La gente de El Paraíso se unió para proteger el recurso vital para la vida y para dar un manejo sustentable a sus bosques. Durante el proceso de reforestación, desde octubre de 2008 hasta septiembre de 2011, los y las comuneros han desarrollado unas destrezas importantes en cuestiones organizativas y prácticas. Por ejemplo, ya establecen viveros forestales de especias nativas que les darán resultados económicos aún después de que el proyecto termine. Al encontrarse en la recta final del proyecto, la comunidad se quedará con una actividad sustentable que genera ingresos.<br /> <br /> <strong>Hacemos microempresas y silvipasturas</strong><br /> En una entrevista el 23 de julio, el presidente de la Unión de Comunidades de Peñaherrera y de El Paraíso, Pedros Bolaños, reveló algunos de los trabajos en que la gente se ha involucrado en la parroquia. Afirmó que &ldquo;nació la iniciativa de consolidar un vivero de producción, no solamente a nivel de la comunidad, pero a nivel de la Zona para abrir más campos hacia otras instituciones, como el Gobierno Provincial de Imbabura y el Ministerio de Medio Ambiente&rdquo;. Dijo, &ldquo;Hemos reclamado que a veces, cuando se hacían proyectos en Íntag, venían los árboles de afuera. Y el Perfecto nos entendió y ya nosotros producimos las plantas y nuestra contraparte es plantarlas en nuestros terrenos&rdquo;.  Añadió que es un camino nuevo porque ya están convirtiéndose en una microempresa para producir árboles para toda la Zona. Incluso, ya el Ministerio de Ambiente les ha pedido 10 000 y el Gobierno Provincial, 35 000 árboles. En el tema del ambiente, aconsejó que no sólo hay que pensar en el aliso, sino &ldquo;hay que tener una variedad, porque sembrar una diversidad la ayuda que se sostenga la vegetación misma&rdquo;. Destacó que es necesario tener una visión del manejo de cada parcela y optimizar tierras para poder poblar las áreas más desprotegidas.<br /> <br /> <strong>Nuestras actividades en forma rutinaria</strong><br /> &ldquo;Tenemos el orden de actividades que tenemos que cumplir todo el año,&rdquo; contó el señor Bolaños.  Todas las semanas tienen cosas que hacer relacionadas con la preparación y la aplicación de abono a la tierra, el riego de las plantas y la planificación de suficientes plantas para la temporada alta. &ldquo;Nuestra meta es siempre tener una base de cien mil plantas&rdquo;, afirmó. La lógica es si tienen la oferta, vendrá la demanda, destacó el líder comunitario.<br /> <strong><br /> Queremos fortalecer a otras comunidades en tema del vivero</strong><br /> Los participantes en el proyecto quieren compartir sus experiencias con otras comunidades en la región. Así Íntag podría convertirse en una Zona de producción de árboles como eje económico. Esto incluye un plan de desarrollo comunitario basado en la solidaridad y ejes también en turismo y actividades ambientales y productivas.<br /> Las mujeres también apoyan el proyecto. Según Rosa Gómez, &ldquo;Después de la amenaza minera, hemos luchado y, aunque el dinero nos hace falta, no rendimos al extractivismo. Al contrario, ya sembramos árboles para que haya agua, paz, tranquilidad, salud y unión. Queremos un futuro sano para nuestros nietos&rdquo;. <br /> *Catedrática en la Universidad de Winona, estado de Minnesota, EE.UU, y gran amiga de la Zona desde hace muchos años.</p>','2011-10-11'),('sistema-de-salud-en-intag','Pablo Vetancourt','sistema de salud en intag',' <p><img class=\"caption\" src=\"/images/stories/portada/intag_hospital_64.jpg\" border=\"0\" align=\"right\" />Unas 250 personas llegaron a la apertura del Centro Hospital de Apuela el 28 de febrero. Durante el evento, las autoridades se comprometieron a seguir trabajando para mejorar la salud en Intag y agradecieron a las personas que gestionaron la obra. Mientras tanto, el equipo médico del Ministerio de Salud ofreció atención a los moradores. Los grupos Amistad e Ideal, oriundos de la zona, y la banda municipal amenizaron la inauguración. Además, Zoila Piedra, de Peñaherrera, presentó una obra cómica relacionada con la Salud.</p> ',' <p><br /><br /><strong>Que los equipos se queden en Intag</strong><br />Oswal Erazo, presidente de la Junta Parroquial de Apuela, dio la bienvenida y agradeció a las autoridades por la construcción de la obra. Señaló que las autoridades, junto con el pueblo, estarán vigilando para que haya un buen funcionamiento del Centro. Además, pidió al Consorcio Toisán que entregue los equipos médicos a la Alcaldía de Cotacachi. Sobre este punto, el Periódico INTAG conversó con José Cueva, presidente del Consorcio Toisán. Aquí cabe un poco de historia. Los equipos, de muy alta tecnología llegaron a través de Solidaridad para el Desarrollo de la Paz (SODEPAZ), una ONG española, debido a la gestión del comité de salud de Cotacachi durante el período del alcalde Auki Tituaña, quien lideró la descentralización de este servicio. Ahora, el Gobierno de Rafael Correa está re-centralizando la salud. Por este motivo, existe el temor de que Intag pierda los equipos tan necesarios para atender bien a la población de la zona. Para evitar esto, el señor Cueva afirmó que los presidentes de las juntas parroquiales inteñas firmaron una resolución en septiembre del año pasado durante una asamblea en García Moreno, según la cual los equipos médicos serán entregados en comodato al Ministerio de Salud Pública (MSP). <br /><br /><strong>Promesas de las autoridades</strong><br />Por su parte, el doctor Gonzalo Jaramillo, director provincial de Salud de Imbabura, señaló que han iniciado la atención aunque faltan algunas adecuaciones, por eso no pudieron entrar a trabajar hace seis meses, como previsto. Además se comprometió en aportar en lo que fuese necesario para el mejoramiento de este espacio de salud. Aseguró que en las próximas semanas, esta unidad de salud estará atendiendo las 24 horas con servicios de ambulancia. Recordó, también, que el aporte desde el Ministerio ha iniciado desde que dotaron de médicos y enfermeras a todos los sub centros de la zona. Ahora las personas no tienen que salir a lugares muy lejanos a buscar atención médica, señaló. Además, manifestó que con el Gobierno actual la salud es gratuita y que es un derecho que el Estado garantiza a la población.<br /><br /><strong>Intervención del financista </strong><br />Francisco Calderón, representante de SODEPAZ, entidad española y financista, aseguró que el sub centro sí está funcionando. Agradeció a las organizaciones sociales de Intag y al Municipio que hicieron posible el proyecto. Asimismo, hizo votos para que la atención en salud mejore. Manifestó que existen algunos espacios que no totalmente equipados y que habrá que hacer algunos cambios. En este tema, el licenciado Alberto Anrango, alcalde de Cotac<img class=\"caption\" src=\"/images/stories/portada/intag_centro salud 1.jpg\" border=\"0\" width=\"376\" height=\"249\" style=\"float: right;\" />achi, aseguró que hará las gestiones para que éstos se arreglen. Manifestó que el trabajo no hará solo en el hospital sino en todos los sub centros de las parroquias. Además, en nombre de la ciudadanía cotacacheña agradeció a SODEPAZ por el apoyo en la construcción de este edificio y la dotación del equipo médico. Finalmente, mencionó que el Municipio asignó 253 500 dólares en las adecuaciones y que habrá más inversión hasta que haya un buen funcionamiento. También, dijo que en el antiguo sub centro de Apuela funcionarán oficinas del Municipio y una unidad de bomberos.<br /><strong><br />Entrevistas con personalidades</strong><br />El médico Humberto Castañeda, de la comunidad Eugenio Espejo, cantón Otavalo, especializado en Cuba, está haciendo el servicio rural en la parroquia de Apuela. En una entrevista con los medios, dijo que el Centro se presta para atender medicina interna, pediatría, ginecología, obstetricia, odontología y urgencias médicas. Las otras especialidades se van a cubrir con brigadas médicas. También afirmó que por ese día estuvieron especialistas ofreciendo atención médica, talleres de nutrición y vacunas para todos los niños. Señaló que el aporte inmediato del MSP es la dotación de medicamentos y personal. Además, aseguró que el Centro será abastecido mensualmente con medicamentos. También, señala que las enfermedades que más ha tratado en la zona son las respiratorias, parasitosis e hipertensión arterial.  <br />Elías Imbaquingo, presidente de la Junta Parroquial de Plaza Gutiérrez, dijo que el Centro todavía no tiene todo lo necesario en términos de instalaciones y equipos. Aseguró que, como representantes del pueblo, los presidentes de las juntas van a seguir haciendo gestiones para solucionar esto. Manifestó que hay que aprovechar la infraestructura y el equipo humano para trabajar con los sub centros. <br />Luis Padilla, morador de Apuela, dijo: “Con la construcción de este hospital en la zona de Intag hemos ganado bastante en el tema de salud…Ojalá haya el personal médico suficiente en cada área de salud”. Dijo que hay que agradecer a las autoridades que se preocuparon de la salud en Intag. Mencionó al ex alcalde, Auki Tituaña, que fue el que gestionó la obra. Afirmó que siempre se ha necesitado un buen sub centro en la zona porque sí ha habido enfermedades graves. Finalmente, recomendó hacer un cerramiento para qué haya más seguridad en el Centro Hospital.<br /><br /><strong>Por qué se demoró la entrega</strong><br />En una entrevista con el Periódico INTAG, el señor Calderón dijo que en el cantón Cotacachi SODEPAZ financió un plan completo de salud cuyo ma<img src=\"/images/stories/portada/intag_centro salud 2.jpg\" border=\"0\" width=\"371\" height=\"248\" style=\"float: right;\" />yor enfoque es la zona de Intag. Dentro de este esfuerzo, que inició hace unos cuatro años, se priorizaron dos obras importantes para la zona de Intag y Los Manduriacos: el sub centro de El Chontal que se inauguró en diciembre y, la más importante, el centro hospital en Apuela. El señor Calderón destacó que este hospital es más grande porque se encuentra en una zona de mayor concentración de la población inteña. También, dentro del proyecto constan equipos para los dos centros. Señaló, además, que el Municipio de Cotacachi dio una parte de la mano de obra y de la infraestructura para los dos centros. Asimismo, dentro de este plan consta educación infantil, talleres de salud y brigadas médicas con especialistas que llegan a todas las comunidades. <br />El señor Calderón aclaró que han hecho modificaciones en el proyecto porque, con la nueva Constitución, el Municipio ha dejado de tener competencias en la salud. Por ejemplo, antes la dotación del personal tenía que hacer el municipio; ahora lo hace el MSP. Las modificaciones mencionadas también afectaron la entrega puntual de la obra. Y también la afectaron las “excusas técnicas” del MSP. Señaló que harán una evaluación para conocer si el funcionamiento, la calidad de la atención y la salud mejoran y la gente se siente contenta con los servicios.<br />En cuanto a futuros aportes en el sector de salud, el señor Calderón dijo que será difícil debido a que no lo manejan los gobiernos seccionales. No obstante, hay la posibilidad de que las parroquias y el Municipio presenten proyectos a SODEPAZ relacionados con la planificación urbana, el turismo, la seguridad alimentaria, entre otros temas.</p>','2010-06-09'),('sobre-sopas-toxicas','Simon Lindenberg','Sobre sopas tóxicas',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/notiparroqcomuni/intag_basura.jpg\" /></p> <p style=\"text-align: justify;\"> Vivimos en un mundo plástico: los zapatos y la ropa que llevamos, los televisores, radios y carros que usamos, hasta las dentaduras postizas que muchos necesitan son de plástico. Desde que conocemos el plástico, nuestras vidas se han vuelto más fáciles. Es un material bastante resistente, pues no se pudre ni se oxida, así que los productos plásticos aguantan mucho tiempo. Muchos envases son de plástico y nos garantizan que la comida que compramos no se dañe pronto.<br /> Pero a pesar de estas y otras ventajas, el plástico causa problemas que aún no se han podido resolver. Mejor dicho, el plástico, combinado con nuestra forma de vivir, causa problemas graves a nivel global.</p> ',' <p>  </p> <p style=\"text-align: justify;\"> Cada día se produce más de 750 mil toneladas de plástico. Por eso que siguen creciendo los basureros sin parar. Una razón son las fundas plásticas: cada uno de nosotros ocupa, como promedio, ocho fundas plásticas diariamente. Pero, ¡una funda plástica no se usa más de ocho minutos, por término medio! En otras palabras, en vez de que una sola funda sirva para las compras durante mucho tiempo, utilizamos muchas fundas plásticas, las botamos y hacemos que se produzcan más y más fundas. Y estas fundas acaban en las calles, el campo y los ríos, contaminando a nuestro medio ambiente. Basta dar un vistazo a una calle inteña...<br /> Muchas personas creen que las fundas plásticas, botadas en la naturaleza, no hacen daño. La razón: suponen que el plástico no se descompone. Esta afirmación, la dieron muchos moradores de Pucará, con quienes hablamos sobre el impacto de la basura plástica en la comunidad. Están equivocados: aunque parezca que las bolsas plásticas no se descomponen, se les salen elementos tóxicos ya que el plástico se hace de petróleo, una sustancia altamente nociva.<br /> Un ejemplo triste e impresionante de los efectos que tiene la basura plástica en el medio ambiente es la gran &ldquo;Sopa de Plástico&rdquo; en el Océano Pacífico. Esta &ldquo;sopa&rdquo; se compone de basura arrojada a los ríos, que luego recorre las vías fluviales y llega al océano. Ahí, la llevan las corrientes marítimas mar adentro. Igual llega toda la basura que ha sido lanzada al mar desde barcos y plataformas petroleras. Cada día se acumula más basura en una superficie que ya es enorme y que sigue creciendo con cada pedazo que llega allá. Incluso hay gente que dice que es más grande que los Estados Unidos. En esta área, ya no se ve el mar por estar cubierto completamente de basura. Y esta basura se descompone por el movimiento, el sol y el agua. En el proceso, sus ingredientes se disuelven, ensuciando y envenenando el agua. Milliones de peces mueren cada día en este campo marítimo de basura. Asimismo, animales como tortugas se enganchan en anillos plásticos cuando son pequeños y, al no poder liberarse, crecen deformes.<br /> Pero los productos plásticos no sólo tienen un impacto grave en la naturaleza que nos rodea, sino también pueden hacernos daño. Las más diminutas partículas de ciertos elementos que se encuentran en el plástico pueden causar la infertilidad de hombres, la diabetes y hasta cambios en la información genética en los óvulos de la mujer y en el semen del hombre, lo que afecta al desarrollo del niño que está por nacer. <br /> Pero, ¿cómo llega el plástico a nuestros cuerpos? ¡Fácil! Los ftalatos, por ejemplo. Esta palabra impronunciable se refiere a sustancias que hacen que el plástico permanezca suave. Los ftalatos son unos de los elementos más dañinos del plástico. Se liberan fácilmente y terminan en el aire. Al respirar, entran en el organismo humano a través de los pulmones. Los ftalatos se encuentran en botas de caucho, en chompas y en envases de alimentos y una infinidad de productos más, así que ¡casi todos tenemos pequeñas cantidades de ellos en nuestros cuerpos!<br /> Hay muchos expertos que advierten de los efectos de los ftalatos. Pero hasta ahora, la industria plástica nos ha convencido que estos elementos son inofensivos.<br /> <br /> <strong>¿Soluciones?</strong><br /> En varios lugares, ya se han tomado medidas para resolver los problemas con el plástico. En Alemania, por ejemplo, hay fábricas grandes donde llega toda la basura plástica. Se clasifica y se recicla para que no acabe en las calles o en el mar. Pero estas fábricas no serán la solución: al reciclar plástico, se empeora la calidad del material porque los elementos tóxicos no desaparecen. Como resultado, los juguetes hechos de plástico reciclado pueden hacer daño a las y los niños si se meten los juguetes en la boca.<br /> Otros países no tienen los recursos financieros que les permitirían tener fábricas de reciclaje. Como consecuencia, la basura se queda en todos lugares: al lado de las calles, en los barrancos, en los ríos, en los parques. En Quito y Guayaquil, por ejemplo, se inundan las calles durante los aguaceros debido a fundas plásticas que luego tapan el alcantarillado. <br /> Para evitar problemas basados en la basura, se ha prohibido en los países africanos Ruanda y Tanzania el uso de fundas plásticas, pues se llenaban del agua de la lluvia y esto posibilitaba la propagación de la malaria. En China y Suráfrica se prohíbe ofrecer fundas plásticas gratis en las tiendas. En Irlanda se cobra por lo menos 30 centavos por funda plástica y este dinero va al Estado como impuesto.<br /> Vivimos en un mundo de plástico: tenemos tanto plástico que no sabemos qué hacer con él. Un grupo de artistas, &ldquo;museo aéreo solar&rdquo;, decidió visualizar cuánta basura nos rodea. Ha ido recogiendo fundas plásticas de la basura, las cortaba y pegaba, y ha creado una construcción gigantesca y sin embargo liviana, que antes de mudarse a otro lugar se infla con aire y luego parece un globo enorme de muchos colores. Desde el año 2007, el grupo viaja por todo el mundo. En cada sitio donde hace escala, construye con las y los habitantes la &lsquo;catedral’ plástica. Esta no dejará de crecer mientras nosotros sigamos derrochando plástico y ensuciando a nuestro planeta. Y la gran &ldquo;Sopa de Plástico&rdquo; en el Océano Pacífico tampoco cesará de crecer...</p> <p>  </p> <p style=\"text-align: justify;\">  </p> <p style=\"text-align: right;\"> <em>Fuentes: Publik Forum, 10/Septiembre/2010: &ldquo;Von Müllstrudeln im Meer und Hormonstoffen im Blut: Wie der allgegenwärtige Kunststoff unsere Welt verändert&rdquo; de Jens Klein und Andrea Teupke http://en.wikipedia.org/wiki/Great_Pacific_Garbage_Patch,<br /> http://es.wikipedia.org/wiki/Sopa_de_plástico, http://es.wikipedia.org/wiki/Ftalato,<br /> http://de.wikipedia.org/wiki/Kunststoff<br /> Imágenes:<br /> http://www.fakeplasticfish.com/images/mae_west_turtle.jpg<br /> </em></p>','2010-11-20'),('tecnico-inteno-viaja-a-argentina','Karen Knee','Técnico Inteño viaja a Argentina',' <p style=\"text-align: center;\"> <img alt=\"alt\" border=\"1\" src=\"/images/stories/cronicaverde/intag_ayala.gif\" /></p> <p style=\"text-align: justify;\"> Desde hace nueve años, Ramiro Ayala ha trabajado en la oficina de la Reserva Ecológica Cotacachi-Cayapas en Cuellaje. Pero entre el 4 de octubre y el 26 de noviembre, su trabajo llevó al técnico del Ministerio del Ambiente al otro extremo del continente. Pasó casi dos meses en la ciudad argentina de Córdova, como parte de un curso sobre el manejo de áreas protegidas latinoamericanas.</p> ',' <p>  </p> <p style=\"text-align: justify;\"> Veinte y tres técnicos medioambientales de 16 países latinoamericanos &ndash;Argentina, Bolivia, Brasil, Chile, Costa Rica, Cuba, Ecuador, El Salvador, Guatemala, México, Nicaragua, Panamá, Paraguay, Perú, la Republica Dominicana y Uruguay&ndash; participaron en el curso. El señor Ayala fue el único representante ecuatoriano. <br /> <br /> <strong>Rappel y dinosaurios</strong><br /> <img align=\"left\" alt=\"alt\" border=\"1\" height=\"263\" hspace=\"8\" src=\"/images/stories/cronicaverde/intag_ayala2.gif\" style=\"width: 233px; height: 263px;\" width=\"233\" />En una entrevista con el Periódico ÍNTAG, el agrónomo explicó que el curso fue el sexto en una serie dictada por la Administración de Parques Nacionales de Argentina, la cual hizo una invitación a todos los países mencionados. Diez personas del Ecuador concursaron. El Departamento de Biodiversidad del Ministerio del Ambiente, en Quito, preseleccionó dos candidatos en base de su experiencia y conocimiento en cuanto al manejo de áreas protegidas, también tomando en cuenta su estado de salud. De los finalistas de cada país, la Administración argentina escogió a los y las participantes. <br /> La Japanese International Cooperation Agency (Agencia Japonesa de Cooperación Internacional; siglas en inglés, JICA) financió el curso. Al ser seleccionado, cada participante recibió una beca que cubrió todos sus gastos: pasaje aéreo, alimentación y hospedaje en una unidad turística propiedad del Ministerio del Turismo de Argentina. Fue la primera vez que el señor Ayala &ndash;quien sólo conocía los países del Ecuador y Colombia&ndash; subió en un avión y viajó a un país lejano. <br /> Los y las integrantes aprendieron sobre la conservación de biodiversidad, la marca legal de administración, la educación ambiental, la señalización, la administración participativa y el co-manejo con comunidades dentro y fuera de la reserva, entre otros temas. Además, salieron al campo para conocer algunos parques nacionales argentinas. Allá, aprendieron a monitorear los lagos, hacer rappel (una manera de bajar acantilados utilizando sogas y poleas) y hacer recorridos y camping con un bajo impacto medioambiental. Un momento cumbre fue la gira al Parque Nacional Talampaya, área protegida donde se encuentran formaciones geológicas únicas que contienen fósiles de dinosaurios. <br /> El señor Ayala usará sus nuevos conocimientos como herramientas que le ayudarán a cumplir la misión de la reserva: proteger los recursos naturales existentes para futuras generaciones bajo un aprovecho sustentable. Uno de los problemas más graves que enfrenta la reserva es la explotación irracional de madera y el avance de la frontera agrícola en zonas frágiles, opinó. Pero mientras espera que lo aprendido le ayude a buscar soluciones a problemas como la tala ilegal, dijo que no implementará cambios específicos como resultado del curso. Toma mucho tiempo y gestión política para cambiar el sistema de manejo que tiene el Ministerio del Ambiente, dijo. <br /> <br /> <em><strong>Pies de fotos: </strong><br /> Los guanacos, parientes de las llamas y alpacas, son parte de la vida silvestre que observó el señor Ayala durante su gira. Foto: Ramiro Ayala. <br /> <br /> El señor Ayala aprendió el rappel, una técnica que utiliza sogas y poleas para bajar acantilados. Foto: Ramiro Ayala. <br /> </em></p>','2011-01-24'),('tengo-que-escribir','Los Editores','TENGO QUE ESCRIBIR!','<p> <!--[if gte mso 9]><xml> <w:WordDocument> <w:View>Normal</w:View> <w:Zoom>0</w:Zoom> <w:TrackMoves/> <w:TrackFormatting/> <w:HyphenationZone>21</w:HyphenationZone> <w:PunctuationKerning/> <w:ValidateAgainstSchemas/> <w:SaveIfXMLInvalid>false</w:SaveIfXMLInvalid> <w:IgnoreMixedContent>false</w:IgnoreMixedContent> <w:AlwaysShowPlaceholderText>false</w:AlwaysShowPlaceholderText> <w:DoNotPromoteQF/> <w:LidThemeOther>ES-EC</w:LidThemeOther> <w:LidThemeAsian>X-NONE</w:LidThemeAsian> <w:LidThemeComplexScript>X-NONE</w:LidThemeComplexScript> <w:Compatibility> <w:BreakWrappedTables/> <w:SnapToGridInCell/> <w:WrapTextWithPunct/> <w:UseAsianBreakRules/> <w:DontGrowAutofit/> <w:SplitPgBreakAndParaMark/> <w:DontVertAlignCellWithSp/> <w:DontBreakConstrainedForcedTables/> <w:DontVertAlignInTxbx/> <w:Word11KerningPairs/> <w:CachedColBalance/> </w:Compatibility> <m:mathPr> <m:mathFont m:val=\"Cambria Math\"/> <m:brkBin m:val=\"before\"/> <m:brkBinSub m:val=\"&#45;-\"/> <m:smallFrac m:val=\"off\"/> <m:dispDef/> <m:lMargin m:val=\"0\"/> <m:rMargin m:val=\"0\"/> <m:defJc m:val=\"centerGroup\"/> <m:wrapIndent m:val=\"1440\"/> <m:intLim m:val=\"subSup\"/> <m:naryLim m:val=\"undOvr\"/> </m:mathPr></w:WordDocument> </xml><![endif]--><!--[if gte mso 9]><xml> <w:LatentStyles DefLockedState=\"false\" DefUnhideWhenUsed=\"true\" DefSemiHidden=\"true\" DefQFormat=\"false\" DefPriority=\"99\" LatentStyleCount=\"267\"> <w:LsdException Locked=\"false\" Priority=\"0\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"Normal\"/> <w:LsdException Locked=\"false\" Priority=\"9\" SemiHidden=\"false\" UnhideWhenUsed=\"false\" QFormat=\"true\" Name=\"heading 1\"/> <w:LsdException Locked=\"false\" Priority=\"9\" QFormat=\"true\" Name=\"heading 2\"/> <w:LsdException Locked=\"false\" Priority=\"9\" QFormat=\"true\" Name=\"heading 3\"/> <w:LsdExcept